"use client";

/**
 * useDashboardWelcomeData - 将 Dashboard 数据转换为欢迎消息格式
 *
 * 从原始 DashboardDTO 提取并转换数据为 ChatWelcomeMessage 所需格式
 */

import { useMemo } from "react";
import type { DashboardDTO } from "@/types/dashboard";

export function useDashboardWelcomeData(dashboard: DashboardDTO | null | undefined) {
  return useMemo(() => {
    if (!dashboard) return null;

    // 提取 bazi skill 数据（假设是 mySkills 的第一个）
    const baziCard = dashboard.mySkills.find(s => s.skillId === "bazi");

    // 构建 fortuneData
    const fortuneData = baziCard ? {
      headline: baziCard.content.headline || "今日运势良好",
      insights: Array.isArray(baziCard.content.insights)
        ? baziCard.content.insights.slice(0, 3)  // 最多 3 条
        : [],
      rating: {
        overall: baziCard.content.rating?.overall || 4,
        focused_area: baziCard.content.rating?.details ? {
          name: "综合能量",
          score: baziCard.content.rating.overall || 4,
          emoji: "🌟",
          tip: baziCard.content.headline || "今日运势良好"
        } : undefined
      }
    } : {
      headline: "开启美好的一天",
      insights: ["保持专注，一步一步来"],
      rating: {
        overall: 4,
        focused_area: {
          name: "综合能量",
          score: 4,
          emoji: "🌟",
          tip: "开启美好的一天"
        }
      }
    };

    return {
      greeting: dashboard.ambient.greeting || "早上好",
      solarTerm: dashboard.ambient.solarTerm,
      streak: dashboard.status.streak || 0,
      fortuneData,
      lifecoachData: dashboard.lifecoach,
      status: dashboard.status
    };
  }, [dashboard]);
}

