    secondary: 'bg-[var(--bg-secondary)] text-[var(--ink-800)] border border-[var(--vellum-300)] hover:bg-[var(--bg-hover)]',
  }
  const sizes = { default: 'px-6 py-3 text-body', large: 'px-8 py-4 text-large' }

  return (
    <motion.div whileHover={{ y: -2 }} whileTap={{ scale: 0.98 }}>
      <Link href={href} className={`${base} ${variants[variant]} ${sizes[size]}`} style={{ boxShadow: 'var(--shadow-card)' }}>
        {children}
      </Link>
    </motion.div>
  )
}

