"""
JWT Token Service
"""
import os
from datetime import datetime, timedelta
from typing import Optional, Dict, Any
from uuid import UUID

import jwt


class JWTService:
    """JWT Token management service"""

    ALGORITHM = "HS256"
    ACCESS_TOKEN_EXPIRE_MINUTES = int(os.getenv("VIBELIFE_ACCESS_TOKEN_EXPIRE_MINUTES", "60"))
    REFRESH_TOKEN_EXPIRE_DAYS = int(os.getenv("VIBELIFE_REFRESH_TOKEN_EXPIRE_DAYS", "30"))

    @classmethod
    def _get_secret(cls) -> str:
        """Get JWT secret - MUST be set in environment"""
        secret = os.getenv("VIBELIFE_JWT_SECRET")
        if not secret:
            raise RuntimeError(
                "CRITICAL: VIBELIFE_JWT_SECRET environment variable is not set. "
                "This is required for secure JWT token generation."
            )
        return secret

    @classmethod
    def create_access_token(
        cls,
        user_id: str,
        vibe_id: str,
        extra_claims: Optional[Dict[str, Any]] = None
    ) -> str:
        """Create access token"""
        expire = datetime.utcnow() + timedelta(minutes=cls.ACCESS_TOKEN_EXPIRE_MINUTES)
        payload = {
            "sub": str(user_id),
            "vibe_id": vibe_id,
            "exp": expire,
            "iat": datetime.utcnow(),
            "type": "access"
        }
        if extra_claims:
            payload.update(extra_claims)
        return jwt.encode(payload, cls._get_secret(), algorithm=cls.ALGORITHM)

    @classmethod
    def create_refresh_token(
        cls,
        user_id: str,
        vibe_id: str
    ) -> str:
        """Create refresh token"""
        expire = datetime.utcnow() + timedelta(days=cls.REFRESH_TOKEN_EXPIRE_DAYS)
        payload = {
            "sub": str(user_id),
            "vibe_id": vibe_id,
            "exp": expire,
            "iat": datetime.utcnow(),
            "type": "refresh"
        }
        return jwt.encode(payload, cls._get_secret(), algorithm=cls.ALGORITHM)

    @classmethod
    def create_sso_token(
        cls,
        user_id: str,
        vibe_id: str,
        target_site: str,
        expire_minutes: int = 5
    ) -> str:
        """Create short-lived SSO token for cross-site auth"""
        expire = datetime.utcnow() + timedelta(minutes=expire_minutes)
        payload = {
            "sub": str(user_id),
            "vibe_id": vibe_id,
            "exp": expire,
            "iat": datetime.utcnow(),
            "type": "sso",
            "target": target_site
        }
        return jwt.encode(payload, cls._get_secret(), algorithm=cls.ALGORITHM)

    @classmethod
    def decode_token(cls, token: str) -> Optional[Dict[str, Any]]:
        """Decode and verify token"""
        try:
            payload = jwt.decode(token, cls._get_secret(), algorithms=[cls.ALGORITHM])
            return payload
        except jwt.ExpiredSignatureError:
            return None
        except jwt.InvalidTokenError:
            return None

    @classmethod
    def verify_access_token(cls, token: str) -> Optional[Dict[str, Any]]:
        """Verify access token and return payload"""
        payload = cls.decode_token(token)
        if payload and payload.get("type") == "access":
            return payload
        return None

    @classmethod
    def verify_refresh_token(cls, token: str) -> Optional[Dict[str, Any]]:
        """Verify refresh token and return payload"""
        payload = cls.decode_token(token)
        if payload and payload.get("type") == "refresh":
            return payload
        return None

    @classmethod
    def verify_sso_token(cls, token: str, target_site: str) -> Optional[Dict[str, Any]]:
        """Verify SSO token for specific site"""
        payload = cls.decode_token(token)
        if payload and payload.get("type") == "sso" and payload.get("target") == target_site:
            return payload
        return None

    @classmethod
    def get_token_expiry(cls) -> int:
        """Get access token expiry in seconds"""
        return cls.ACCESS_TOKEN_EXPIRE_MINUTES * 60

