"use client";

/**
 * Login Page - v7.1 设计系统
 *
 * v7.1 优化:
 * - 使用 AuthLayout 左右分栏布局
 * - 无边框设计 + 多层阴影
 * - memo 组件优化
 */

import { useState, useCallback, memo } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { login } from "@/lib/api";
import { OAuthButtons } from "@/components/auth/OAuthButtons";
import {
  AuthLayout,
  AuthCard,
  AuthDivider,
  AuthLink,
} from "@/components/auth/AuthLayout";

// ═══════════════════════════════════════════════════════════════════════════
// Login Form Component
// ═══════════════════════════════════════════════════════════════════════════

interface LoginFormProps {
  onSubmit: (email: string, password: string) => Promise<void>;
  onOAuthSuccess: () => void;
  onOAuthError: (error: string) => void;
  error: string;
  isLoading: boolean;
}

const LoginForm = memo(function LoginForm({
  onSubmit,
  onOAuthSuccess,
  onOAuthError,
  error,
  isLoading,
}: LoginFormProps) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    await onSubmit(email, password);
  }, [email, password, onSubmit]);

  return (
    <>
      {/* OAuth Buttons */}
      <OAuthButtons
        onSuccess={onOAuthSuccess}
        onError={onOAuthError}
        disabled={isLoading}
      />

      <AuthDivider />

      {/* Email/Password Form */}
      <form onSubmit={handleSubmit} className="space-y-4">
        <Input
          type="email"
          label="邮箱"
          placeholder="your@email.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={isLoading}
        />
        <Input
          type="password"
          label="密码"
          placeholder="输入密码"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={isLoading}
        />

        {error && (
          <div className="p-3 rounded-lg bg-error/10 border border-error/20">
            <p className="text-error text-sm text-center">{error}</p>
          </div>
        )}

        <Button
          type="submit"
          variant="primary"
          className="w-full"
          isLoading={isLoading}
        >
          登录
        </Button>
      </form>

      {/* Forgot Password */}
      <div className="mt-4 text-center">
        <Link
          href="/auth/forgot-password"
          className="text-sm text-text-secondary hover:text-accent-primary transition-colors"
        >
          忘记密码？
        </Link>
      </div>
    </>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// Login Page
// ═══════════════════════════════════════════════════════════════════════════

export default function LoginPage() {
  const router = useRouter();
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = useCallback(async (email: string, password: string) => {
    setError("");
    setIsLoading(true);

    try {
      await login(email, password);
      router.push("/");
    } catch (err) {
      setError(err instanceof Error ? err.message : "登录失败，请重试");
    } finally {
      setIsLoading(false);
    }
  }, [router]);

  const handleOAuthSuccess = useCallback(() => {
    router.push("/");
  }, [router]);

  const handleOAuthError = useCallback((errorMsg: string) => {
    setError(errorMsg);
  }, []);

  const handleRegisterClick = useCallback(() => {
    router.push("/auth/register");
  }, [router]);

  return (
    <AuthLayout>
      <AuthCard
        title="欢迎回来"
        subtitle="登录你的 VibeLife 账号"
        footer={
          <AuthLink
            text="还没有账号？"
            linkText="立即注册"
            href="/auth/register"
            onClick={handleRegisterClick}
          />
        }
      >
        <LoginForm
          onSubmit={handleSubmit}
          onOAuthSuccess={handleOAuthSuccess}
          onOAuthError={handleOAuthError}
          error={error}
          isLoading={isLoading}
        />
      </AuthCard>
    </AuthLayout>
  );
}

