     1→/**
     2→ * OAuth Buttons Component
     3→ * Google and Apple sign-in buttons
     4→ */
     5→"use client";
     6→
     7→import { useState } from "react";
     8→import { Button } from "@/components/ui/Button";
     9→import { googleAuth, appleAuth, OnboardingData } from "@/lib/api";
    10→
    11→interface OAuthButtonsProps {
    12→  onSuccess?: () => void;
    13→  onError?: (error: string) => void;
    14→  onboarding?: OnboardingData;
    15→  disabled?: boolean;
    16→}
    17→
    18→// Google OAuth configuration
    19→const GOOGLE_CLIENT_ID = process.env.NEXT_PUBLIC_GOOGLE_CLIENT_ID || "";
    20→
    21→export function OAuthButtons({
    22→  onSuccess,
    23→  onError,
    24→  onboarding,
    25→  disabled = false,
    26→}: OAuthButtonsProps) {
    27→  const [isGoogleLoading, setIsGoogleLoading] = useState(false);
    28→  const [isAppleLoading, setIsAppleLoading] = useState(false);
    29→
    30→  // Google Sign-In
    31→  const handleGoogleSignIn = async () => {
    32→    if (!GOOGLE_CLIENT_ID) {
    33→      onError?.("Google OAuth not configured");
    34→      return;
    35→    }
    36→
    37→    setIsGoogleLoading(true);
    38→
    39→    try {
    40→      // Load Google Identity Services
    41→      const google = (window as unknown as { google?: { accounts: { id: { initialize: (config: { client_id: string; callback: (response: { credential: string }) => void }) => void; prompt: () => void } } } }).google;
    42→
    43→      if (!google) {
    44→        // Load the script if not already loaded
    45→        await loadGoogleScript();
    46→      }
    47→
    48→      // Initialize and prompt
    49→      const googleAccounts = (window as unknown as { google: { accounts: { id: { initialize: (config: { client_id: string; callback: (response: { credential: string }) => void }) => void; prompt: () => void } } } }).google.accounts.id;
    50→
    51→      googleAccounts.initialize({
    52→        client_id: GOOGLE_CLIENT_ID,
    53→        callback: async (response: { credential: string }) => {
    54→          try {
    55→            await googleAuth(response.credential, onboarding);
    56→            onSuccess?.();
    57→          } catch (err) {
    58→            onError?.(err instanceof Error ? err.message : "Google auth failed");
    59→          } finally {
    60→            setIsGoogleLoading(false);
    61→          }
    62→        },
    63→      });
    64→
    65→      googleAccounts.prompt();
    66→    } catch (err) {
    67→      onError?.(err instanceof Error ? err.message : "Google sign-in failed");
    68→      setIsGoogleLoading(false);
    69→    }
    70→  };
    71→
    72→  // Apple Sign-In
    73→  const handleAppleSignIn = async () => {
    74→    setIsAppleLoading(true);
    75→
    76→    try {
    77→      // Load Apple JS SDK if not loaded
    78→      const AppleID = (window as unknown as { AppleID?: { auth: { init: (config: { clientId: string; scope: string; redirectURI: string; usePopup: boolean }) => void; signIn: () => Promise<{ authorization: { id_token: string }; user?: { name?: { firstName?: string; lastName?: string } } }> } } }).AppleID;
    79→
    80→      if (!AppleID) {
    81→        await loadAppleScript();
    82→      }
    83→
    84→      const appleAuth_ = (window as unknown as { AppleID: { auth: { init: (config: { clientId: string; scope: string; redirectURI: string; usePopup: boolean }) => void; signIn: () => Promise<{ authorization: { id_token: string }; user?: { name?: { firstName?: string; lastName?: string } } }> } } }).AppleID.auth;
    85→
    86→      appleAuth_.init({
    87→        clientId: process.env.NEXT_PUBLIC_APPLE_CLIENT_ID || "",
    88→        scope: "name email",
    89→        redirectURI: `${window.location.origin}/auth/apple-callback`,
    90→        usePopup: true,
    91→      });
    92→
    93→      const response = await appleAuth_.signIn();
    94→      const idToken = response.authorization.id_token;
    95→      const userName = response.user?.name
    96→        ? `${response.user.name.firstName || ""} ${response.user.name.lastName || ""}`.trim()
    97→        : undefined;
    98→
    99→      await appleAuth(idToken, userName, onboarding);
   100→      onSuccess?.();
   101→    } catch (err) {
   102→      // User cancelled or error
   103→      if ((err as { error?: string })?.error !== "popup_closed_by_user") {
   104→        onError?.(err instanceof Error ? err.message : "Apple sign-in failed");
   105→      }
   106→    } finally {
   107→      setIsAppleLoading(false);
   108→    }
   109→  };
   110→
   111→  return (
   112→    <div className="space-y-3">
   113→      {/* Google Sign-In */}
   114→      <Button
   115→        type="button"
   116→        variant="secondary"
   117→        className="w-full flex items-center justify-center gap-2"
   118→        onClick={handleGoogleSignIn}
   119→        disabled={disabled || isGoogleLoading}
   120→        isLoading={isGoogleLoading}
   121→      >
   122→        <GoogleIcon />
   123→        Continue with Google
   124→      </Button>
   125→
   126→      {/* Apple Sign-In */}
   127→      <Button
   128→        type="button"
   129→        variant="secondary"
   130→        className="w-full flex items-center justify-center gap-2"
   131→        onClick={handleAppleSignIn}
   132→        disabled={disabled || isAppleLoading}
   133→        isLoading={isAppleLoading}
   134→      >
   135→        <AppleIcon />
   136→        Continue with Apple
   137→      </Button>
   138→    </div>
   139→  );
   140→}
   141→
   142→// Helper to load Google Identity Services script
   143→function loadGoogleScript(): Promise<void> {
   144→  return new Promise((resolve, reject) => {
   145→    if (document.getElementById("google-identity-script")) {
   146→      resolve();
   147→      return;
   148→    }
   149→
   150→    const script = document.createElement("script");
   151→    script.id = "google-identity-script";
   152→    script.src = "https://accounts.google.com/gsi/client";
   153→    script.async = true;
   154→    script.defer = true;
   155→    script.onload = () => resolve();
   156→    script.onerror = () => reject(new Error("Failed to load Google script"));
   157→    document.head.appendChild(script);
   158→  });
   159→}
   160→
   161→// Helper to load Apple JS SDK
   162→function loadAppleScript(): Promise<void> {
   163→  return new Promise((resolve, reject) => {
   164→    if (document.getElementById("apple-signin-script")) {
   165→      resolve();
   166→      return;
   167→    }
   168→
   169→    const script = document.createElement("script");
   170→    script.id = "apple-signin-script";
   171→    script.src = "https://appleid.cdn-apple.com/appleauth/static/jsapi/appleid/1/en_US/appleid.auth.js";
   172→    script.async = true;
   173→    script.defer = true;
   174→    script.onload = () => resolve();
   175→    script.onerror = () => reject(new Error("Failed to load Apple script"));
   176→    document.head.appendChild(script);
   177→  });
   178→}
   179→
   180→// Google Icon
   181→function GoogleIcon() {
   182→  return (
   183→    <svg width="18" height="18" viewBox="0 0 18 18" fill="none">
   184→      <path
   185→        d="M17.64 9.2c0-.637-.057-1.251-.164-1.84H9v3.481h4.844c-.209 1.125-.843 2.078-1.796 2.717v2.258h2.908c1.702-1.567 2.684-3.874 2.684-6.615z"
   186→        fill="#4285F4"
   187→      />
   188→      <path
   189→        d="M9.003 18c2.43 0 4.467-.806 5.956-2.18l-2.909-2.26c-.806.54-1.836.86-3.047.86-2.344 0-4.328-1.584-5.036-3.711H.96v2.332A8.997 8.997 0 009.003 18z"
   190→        fill="#34A853"
   191→      />
   192→      <path
   193→        d="M3.964 10.712A5.41 5.41 0 013.682 9c0-.593.102-1.17.282-1.71V4.958H.957A8.996 8.996 0 000 9c0 1.452.348 2.827.957 4.042l3.007-2.33z"
   194→        fill="#FBBC05"
   195→      />
   196→      <path
   197→        d="M9.003 3.58c1.321 0 2.508.454 3.44 1.345l2.582-2.58C13.464.891 11.428 0 9.002 0A8.997 8.997 0 00.957 4.958L3.964 7.29c.708-2.127 2.692-3.71 5.036-3.71z"
   198→        fill="#EA4335"
   199→      />
   200→    </svg>
   201→  );
   202→}
   203→
   204→// Apple Icon
   205→function AppleIcon() {
