     1→/**
     2→ * Authentication Provider - v5.2 性能优化版
     3→ * Based on: vibelife spec v3.0
     4→ *
     5→ * v3.0 更新：
     6→ * - 实现真实的认证状态管理
     7→ * - 支持多标签页同步
     8→ * - 基于 JWT + localStorage
     9→ *
    10→ * v5.2 优化：
    11→ * - 使用缓存的 localStorage 工具 (Vercel rule: js-cache-storage)
    12→ * - 优化初始化流程，先显示缓存用户再后台验证
    13→ */
    14→"use client";
    15→
    16→import React, {
    17→  createContext,
    18→  useContext,
    19→  useState,
    20→  useEffect,
    21→  useCallback,
    22→  useMemo,
    23→} from "react";
    24→import {
    25→  getTokens,
    26→  setTokens,
    27→  clearTokens,
    28→  getMe,
    29→  login as apiLogin,
    30→  logout as apiLogout,
    31→  register as apiRegister,
    32→  registerWithSession as apiRegisterWithSession,
    33→  type TokenResponse,
    34→  type OnboardingData,
    35→} from "@/lib/api";
    36→import {
    37→  getLocalStorageJSON,
    38→  setLocalStorageJSON,
    39→  getLocalStorage,
    40→  invalidateStorageCache,
    41→} from "@/utils/storage";
    42→
    43→// ─────────────────────────────────────────────────────────────────
    44→// Types
    45→// ─────────────────────────────────────────────────────────────────
    46→
    47→export interface User {
    48→  user_id: string;
    49→  vibe_id: string;
    50→  display_name?: string;
    51→  email?: string;
    52→  phone?: string;
    53→}
    54→
    55→interface AuthState {
    56→  user: User | null;
    57→  isAuthenticated: boolean;
    58→  isLoading: boolean;
    59→}
    60→
    61→interface AuthActions {
    62→  login: (email: string, password: string) => Promise<void>;
    63→  logout: () => Promise<void>;
    64→  register: (
    65→    email?: string,
    66→    password?: string,
    67→    displayName?: string,
    68→    phone?: string
    69→  ) => Promise<void>;
    70→  registerWithSession: (
    71→    email?: string,
    72→    password?: string,
    73→    displayName?: string,
    74→    phone?: string,
    75→    sessionId?: string,
    76→    onboarding?: OnboardingData
    77→  ) => Promise<void>;
    78→  refreshUser: () => Promise<void>;
    79→}
    80→
    81→type AuthContextType = AuthState & AuthActions;
    82→
    83→// ─────────────────────────────────────────────────────────────────
    84→// Context
    85→// ─────────────────────────────────────────────────────────────────
    86→
    87→const AuthContext = createContext<AuthContextType | null>(null);
    88→
    89→// ─────────────────────────────────────────────────────────────────
    90→// Provider
    91→// ─────────────────────────────────────────────────────────────────
    92→
    93→interface AuthProviderProps {
    94→  children: React.ReactNode;
    95→}
    96→
    97→export function AuthProvider({ children }: AuthProviderProps) {
    98→  const [state, setState] = useState<AuthState>({
    99→    user: null,
   100→    isAuthenticated: false,
   101→    isLoading: true,
   102→  });
   103→
   104→  // Initialize auth state from localStorage
   105→  const initAuth = useCallback(async () => {
   106→    const { accessToken } = getTokens();
   107→
   108→    if (!accessToken) {
   109→      setState({ user: null, isAuthenticated: false, isLoading: false });
   110→      return;
   111→    }
   112→
   113→    try {
   114→      // Use cached localStorage read (Vercel rule: js-cache-storage)
   115→      const cachedUser = getLocalStorageJSON<User | null>("user", null);
   116→      if (cachedUser) {
   117→        // Show cached user immediately (faster perceived load)
   118→        setState({ user: cachedUser, isAuthenticated: true, isLoading: false });
   119→      }
   120→
   121→      // Then verify with backend (in background)
   122→      const userData = await getMe();
   123→      const user: User = {
   124→        user_id: userData.user_id,
   125→        vibe_id: userData.vibe_id,
   126→        display_name: userData.display_name,
   127→        email: userData.email,
   128→        phone: userData.phone,
   129→      };
   130→
   131→      // Update localStorage and state using cached utility
   132→      setLocalStorageJSON("user", user);
   133→      setState({ user, isAuthenticated: true, isLoading: false });
   134→    } catch {
   135→      // Token invalid, clear everything
   136→      clearTokens();
   137→      invalidateStorageCache("user");
   138→      setState({ user: null, isAuthenticated: false, isLoading: false });
   139→    }
   140→  }, []);
   141→
   142→  // Initialize on mount
   143→  useEffect(() => {
   144→    initAuth();
   145→  }, [initAuth]);
   146→
   147→  // Listen for storage events (multi-tab sync)
   148→  useEffect(() => {
   149→    const handleStorageChange = (e: StorageEvent) => {
   150→      if (e.key === "access_token") {
   151→        if (!e.newValue) {
   152→          // Token was removed (logout in another tab)
   153→          setState({ user: null, isAuthenticated: false, isLoading: false });
   154→        } else if (e.newValue && !e.oldValue) {
   155→          // Token was added (login in another tab)
   156→          initAuth();
   157→        }
   158→      }
   159→
   160→      if (e.key === "user") {
   161→        if (e.newValue) {
   162→          try {
   163→            const user = JSON.parse(e.newValue) as User;
   164→            setState((prev) => ({ ...prev, user, isAuthenticated: true }));
   165→          } catch {
   166→            // Invalid JSON, ignore
   167→          }
   168→        }
   169→      }
   170→    };
   171→
   172→    window.addEventListener("storage", handleStorageChange);
   173→    return () => window.removeEventListener("storage", handleStorageChange);
   174→  }, [initAuth]);
   175→
   176→  // ─────────────────────────────────────────────────────────────────
   177→  // Actions
   178→  // ─────────────────────────────────────────────────────────────────
   179→
   180→  const login = useCallback(async (email: string, password: string) => {
   181→    setState((prev) => ({ ...prev, isLoading: true }));
   182→
   183→    try {
   184→      const response: TokenResponse = await apiLogin(email, password);
   185→      const user: User = {
   186→        user_id: response.user.user_id,
   187→        vibe_id: response.user.vibe_id,
   188→        display_name: response.user.display_name,
   189→      };
   190→
   191→      setState({ user, isAuthenticated: true, isLoading: false });
   192→    } catch (error) {
   193→      setState((prev) => ({ ...prev, isLoading: false }));
   194→      throw error;
   195→    }
   196→  }, []);
   197→
   198→  const logout = useCallback(async () => {
   199→    setState((prev) => ({ ...prev, isLoading: true }));
   200→
   201→    try {
   202→      await apiLogout();
   203→    } finally {
   204→      setState({ user: null, isAuthenticated: false, isLoading: false });
   205→    }
   206→  }, []);
   207→
   208→  const register = useCallback(
   209→    async (
   210→      email?: string,
   211→      password?: string,
   212→      displayName?: string,
   213→      phone?: string
   214→    ) => {
   215→      setState((prev) => ({ ...prev, isLoading: true }));
   216→
   217→      try {
   218→        const response: TokenResponse = await apiRegister(
   219→          email,
   220→          password,
   221→          displayName,
   222→          phone
   223→        );
   224→        const user: User = {
   225→          user_id: response.user.user_id,
   226→          vibe_id: response.user.vibe_id,
   227→          display_name: response.user.display_name,
   228→        };
   229→
   230→        setState({ user, isAuthenticated: true, isLoading: false });
   231→      } catch (error) {
   232→        setState((prev) => ({ ...prev, isLoading: false }));
   233→        throw error;
   234→      }
   235→    },
   236→    []
   237→  );
   238→
   239→  const registerWithSession = useCallback(
   240→    async (
   241→      email?: string,
   242→      password?: string,
   243→      displayName?: string,
   244→      phone?: string,
   245→      sessionId?: string,
   246→      onboarding?: OnboardingData
   247→    ) => {
   248→      setState((prev) => ({ ...prev, isLoading: true }));
   249→
   250→      try {
   251→        const response: TokenResponse = await apiRegisterWithSession(
   252→          email,
   253→          password,
   254→          displayName,
   255→          phone,
   256→          sessionId,
   257→          onboarding
   258→        );
   259→        const user: User = {
   260→          user_id: response.user.user_id,
   261→          vibe_id: response.user.vibe_id,
   262→          display_name: response.user.display_name,
   263→        };
   264→
   265→        setState({ user, isAuthenticated: true, isLoading: false });
   266→      } catch (error) {
   267→        setState((prev) => ({ ...prev, isLoading: false }));
   268→        throw error;
   269→      }
   270→    },
   271→    []
   272→  );
   273→
   274→  const refreshUser = useCallback(async () => {
   275→    try {
   276→      const userData = await getMe();
   277→      const user: User = {
   278→        user_id: userData.user_id,
   279→        vibe_id: userData.vibe_id,
   280→        display_name: userData.display_name,
   281→        email: userData.email,
   282→        phone: userData.phone,
   283→      };
   284→
   285→      // Use cached localStorage (Vercel rule: js-cache-storage)
   286→      setLocalStorageJSON("user", user);
   287→      setState((prev) => ({ ...prev, user }));
   288→    } catch {
   289→      // Silently fail - user data refresh is not critical
   290→    }
   291→  }, []);
   292→
   293→  // ─────────────────────────────────────────────────────────────────
   294→  // Memoized value
   295→  // ─────────────────────────────────────────────────────────────────
   296→
   297→  const value = useMemo<AuthContextType>(
   298→    () => ({
   299→      ...state,
   300→      login,
   301→      logout,
   302→      register,
   303→      registerWithSession,
   304→      refreshUser,
   305→    }),
   306→    [state, login, logout, register, registerWithSession, refreshUser]
   307→  );
   308→
   309→  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
   310→}
   311→
   312→// ─────────────────────────────────────────────────────────────────
   313→// Hooks
   314→// ─────────────────────────────────────────────────────────────────
   315→
   316→export function useAuth() {
   317→  const context = useContext(AuthContext);
   318→
   319→  if (!context) {
   320→    throw new Error("useAuth must be used within an AuthProvider");
   321→  }
   322→
   323→  return {
   324→    isSignedIn: context.isAuthenticated,
   325→    isLoaded: !context.isLoading,
   326→    userId: context.user?.user_id ?? null,
   327→    user: context.user,
   328→    login: context.login,
   329→    logout: context.logout,
   330→    register: context.register,
   331→    registerWithSession: context.registerWithSession,
   332→    refreshUser: context.refreshUser,
   333→  };
   334→}
   335→
   336→export function useUser() {
   337→  const context = useContext(AuthContext);
   338→
   339→  if (!context) {
   340→    throw new Error("useUser must be used within an AuthProvider");
   341→  }
   342→
   343→  return {
   344→    user: context.user,
   345→    isLoaded: !context.isLoading,
   346→  };
   347→}
   348→
   349→// Clerk compatibility shims
   350→export function useClerk() {
   351→  const { logout } = useAuth();
   352→  return {
   353→    signOut: logout,
   354→  };
   355→}
   356→
   357→// Placeholder components for Clerk compatibility
   358→export function SignIn() {
   359→  return null;
   360→}
   361→
   362→export function SignUp() {
   363→  return null;
   364→}
   365→
   366→export function UserButton() {
   367→  return null;
   368→}
