   260→export interface StreamChatResult {
   261→  conversation_id?: string;
   262→  skill?: string;
   263→  voice_mode?: string;
   264→}
   265→
   266→export async function streamChat(
   267→  request: ChatRequest,
   268→  onChunk: (chunk: string) => void,
   269→  onDone: (result: StreamChatResult) => void,
   270→  onError: (error: Error) => void
   271→): Promise<void> {
   272→  const { accessToken } = getTokens();
   273→
   274→  try {
   275→    const response = await fetch("/api/chat", {
   276→      method: "POST",
   277→      headers: {
   278→        "Content-Type": "application/json",
   279→        ...(accessToken && { Authorization: `Bearer ${accessToken}` }),
   280→      },
   281→      body: JSON.stringify(request),
   282→    });
   283→
   284→    if (!response.ok) {
   285→      const error = await response.json().catch(() => ({ detail: "Stream failed" }));
   286→      onError(new Error(error.detail || "Stream failed"));
   287→      return;
   288→    }
   289→
   290→    const reader = response.body?.getReader();
   291→    const decoder = new TextDecoder();
   292→
   293→    if (!reader) {
   294→      onError(new Error("No response body"));
   295→      return;
   296→    }
   297→
   298→    let buffer = "";
   299→
   300→    while (true) {
   301→      const { done, value } = await reader.read();
   302→      if (done) break;
   303→
   304→      buffer += decoder.decode(value, { stream: true });
   305→      const lines = buffer.split("\n");
   306→      buffer = lines.pop() || "";
   307→
   308→      for (const line of lines) {
   309→        if (line.startsWith("data: ")) {
   310→          const dataStr = line.slice(6).trim();
   311→          if (!dataStr) continue;
   312→
   313→          try {
   314→            const data = JSON.parse(dataStr);
   315→            if (data.type === "chunk") {
   316→              onChunk(data.content);
   317→            } else if (data.type === "done") {
   318→              onDone({
   319→                conversation_id: data.conversation_id,
   320→                skill: data.skill,
   321→                voice_mode: data.voice_mode,
   322→              });
   323→              return;
   324→            } else if (data.type === "error") {
   325→              onError(new Error(data.message));
   326→              return;
   327→            }
   328→          } catch {
   329→            // Skip malformed JSON
   330→          }
   331→        }
   332→      }
   333→    }
   334→
   335→    onDone({});
   336→  } catch (error) {
   337→    onError(error instanceof Error ? error : new Error(String(error)));
   338→  }
   339→}
