"use client";

/**
 * Register Page - v7.1 设计系统
 *
 * v7.1 优化:
 * - 使用 AuthLayout 左右分栏布局
 * - 无边框设计 + 多层阴影
 * - memo 组件优化
 */

import { useState, useCallback, memo } from "react";
import { useRouter } from "next/navigation";
import { Button } from "@/components/ui/Button";
import { Input } from "@/components/ui/Input";
import { register } from "@/lib/api";
import {
  AuthLayout,
  AuthCard,
  AuthLink,
} from "@/components/auth/AuthLayout";
import { cn } from "@/lib/utils";

type RegisterMode = "email" | "phone";

// ═══════════════════════════════════════════════════════════════════════════
// Mode Toggle Component
// ═══════════════════════════════════════════════════════════════════════════

interface ModeToggleProps {
  mode: RegisterMode;
  onModeChange: (mode: RegisterMode) => void;
  disabled?: boolean;
}

const ModeToggle = memo(function ModeToggle({
  mode,
  onModeChange,
  disabled,
}: ModeToggleProps) {
  return (
    <div className="flex mb-6 bg-bg-secondary rounded-xl p-1">
      <button
        type="button"
        onClick={() => onModeChange("email")}
        disabled={disabled}
        className={cn(
          "flex-1 py-2.5 text-sm font-medium rounded-lg transition-all duration-fast",
          mode === "email"
            ? "bg-bg-card text-text-primary shadow-card"
            : "text-text-secondary hover:text-text-primary"
        )}
      >
        邮箱注册
      </button>
      <button
        type="button"
        onClick={() => onModeChange("phone")}
        disabled={disabled}
        className={cn(
          "flex-1 py-2.5 text-sm font-medium rounded-lg transition-all duration-fast",
          mode === "phone"
            ? "bg-bg-card text-text-primary shadow-card"
            : "text-text-secondary hover:text-text-primary"
        )}
      >
        手机号注册
      </button>
    </div>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// Register Form Component
// ═══════════════════════════════════════════════════════════════════════════

interface RegisterFormProps {
  onSubmit: (data: {
    mode: RegisterMode;
    email: string;
    phone: string;
    password: string;
    displayName: string;
  }) => Promise<void>;
  error: string;
  isLoading: boolean;
}

const RegisterForm = memo(function RegisterForm({
  onSubmit,
  error,
  isLoading,
}: RegisterFormProps) {
  const [mode, setMode] = useState<RegisterMode>("email");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [displayName, setDisplayName] = useState("");
  const [localError, setLocalError] = useState("");

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    setLocalError("");

    // Validation
    if (password !== confirmPassword) {
      setLocalError("两次密码输入不一致");
      return;
    }

    if (password.length < 6) {
      setLocalError("密码至少需要6个字符");
      return;
    }

    if (mode === "phone" && phone.length < 11) {
      setLocalError("请输入正确的手机号");
      return;
    }

    if (mode === "email" && !email) {
      setLocalError("请输入邮箱地址");
      return;
    }

    await onSubmit({ mode, email, phone, password, displayName });
  }, [mode, email, phone, password, confirmPassword, displayName, onSubmit]);

  const displayError = localError || error;

  return (
    <>
      <ModeToggle mode={mode} onModeChange={setMode} disabled={isLoading} />

      <form onSubmit={handleSubmit} className="space-y-4">
        <Input
          type="text"
          label="昵称（可选）"
          placeholder="你想被怎么称呼？"
          value={displayName}
          onChange={(e) => setDisplayName(e.target.value)}
          disabled={isLoading}
        />

        {mode === "email" ? (
          <Input
            type="email"
            label="邮箱"
            placeholder="your@email.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={isLoading}
          />
        ) : (
          <div>
            <label className="block text-sm font-medium text-text-secondary mb-1.5">
              手机号
            </label>
            <div className="relative">
              <span className="absolute left-4 top-1/2 -translate-y-1/2 text-text-tertiary text-sm">
                +86
              </span>
              <input
                type="tel"
                value={phone}
                onChange={(e) => setPhone(e.target.value.replace(/\D/g, "").slice(0, 11))}
                placeholder="请输入手机号"
                className={cn(
                  "w-full rounded-lg pl-12 pr-4 py-2.5 h-10",
                  "bg-bg-card shadow-card",
                  "font-sans text-base text-text-primary",
                  "placeholder:text-text-disabled",
                  "transition-all duration-fast ease-out-expo",
                  "focus:outline-none focus:shadow-card-hover focus:ring-2 focus:ring-accent/20",
                  "disabled:opacity-50 disabled:cursor-not-allowed"
                )}
                required
                disabled={isLoading}
              />
            </div>
          </div>
        )}

        <Input
          type="password"
          label="密码"
          placeholder="至少6个字符"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={isLoading}
        />

        <Input
          type="password"
          label="确认密码"
          placeholder="再次输入密码"
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
          disabled={isLoading}
        />

        {displayError && (
          <div className="p-3 rounded-lg bg-error/10 border border-error/20">
            <p className="text-error text-sm text-center">{displayError}</p>
          </div>
        )}

        <Button
          type="submit"
          variant="primary"
          className="w-full"
          isLoading={isLoading}
        >
          创建账号
        </Button>
      </form>

      {/* Terms */}
      <p className="mt-4 text-xs text-text-tertiary text-center">
        注册即表示你同意我们的{" "}
        <a href="/terms" className="text-accent-primary hover:underline">服务条款</a>
        {" "}和{" "}
        <a href="/privacy" className="text-accent-primary hover:underline">隐私政策</a>
      </p>
    </>
  );
});

// ═══════════════════════════════════════════════════════════════════════════
// Register Page
// ═══════════════════════════════════════════════════════════════════════════

export default function RegisterPage() {
  const router = useRouter();
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = useCallback(async (data: {
    mode: RegisterMode;
    email: string;
    phone: string;
    password: string;
    displayName: string;
  }) => {
    setError("");
    setIsLoading(true);

    try {
      await register(
        data.mode === "email" ? data.email : undefined,
        data.password,
        data.displayName || undefined,
        data.mode === "phone" ? `+86${data.phone}` : undefined
      );
      router.push("/");
    } catch (err) {
      setError(err instanceof Error ? err.message : "注册失败，请重试");
    } finally {
      setIsLoading(false);
    }
  }, [router]);

  const handleLoginClick = useCallback(() => {
    router.push("/auth/login");
  }, [router]);

  return (
    <AuthLayout>
      <AuthCard
        title="创建账号"
        subtitle="开始你的 VibeLife 之旅"
        footer={
          <AuthLink
            text="已有账号？"
            linkText="立即登录"
            href="/auth/login"
            onClick={handleLoginClick}
          />
        }
      >
        <RegisterForm
          onSubmit={handleSubmit}
          error={error}
          isLoading={isLoading}
        />
      </AuthCard>
    </AuthLayout>
  );
}

'use client'

/**
 * Settings Page - v5.1 性能优化版
 * 设计文档: payment-design-pc.md 第十章
 *
 * v5.1 优化:
 * - 移除 framer-motion，使用 CSS 动画 (Vercel rule: bundle-dynamic-imports)
 * - 使用缓存的 localStorage 读取 (Vercel rule: js-cache-storage)
 * - 使用函数式 setState (Vercel rule: rerender-functional-setstate)
 *
 * PC端布局: 左侧设置菜单 + 右侧设置内容
 * 移动端布局: 单栏设置列表
 */

import { useState, useEffect, useCallback, memo } from 'react'
import { useRouter } from 'next/navigation'
import { PCLayout } from '@/components/layout/PCLayout'
import { getLocalStorageJSON, getAccessToken, setLocalStorageJSON } from '@/utils/storage'

const API_BASE = process.env.NEXT_PUBLIC_API_BASE || 'http://localhost:8000/api/v1'

const SETTINGS_MENU = [
  { id: 'profile', icon: '👤', label: '个人资料' },
  { id: 'notifications', icon: '🔔', label: '通知设置' },
  { id: 'privacy', icon: '🔐', label: '隐私与安全' },
  { id: 'membership', icon: '💎', label: '会员管理' },
  { id: 'memory', icon: '🧠', label: '记忆管理' },
  { id: 'preferences', icon: '⚙️', label: '偏好设置' },
  { id: 'help', icon: '❓', label: '帮助与反馈' },
  { id: 'about', icon: '📄', label: '关于' },
]

interface UserProfile {
  nickname: string
  email: string
  avatar: string
  birthDate: string
  birthTime: string
  timezone: string
}

export default function SettingsPage() {
  const router = useRouter()
  const [activeSection, setActiveSection] = useState('profile')
  const [loading, setLoading] = useState(false)
  const [profile, setProfile] = useState<UserProfile>(() => ({
    nickname: '用户',
    email: 'user@example.com',
    avatar: '👤',
    birthDate: '1990-05-15',
    birthTime: '10:30',
    timezone: 'Asia/Shanghai',
  }))

  useEffect(() => {
    // Use cached localStorage read (Vercel rule: js-cache-storage)
    const user = getLocalStorageJSON<Record<string, string> | null>('user', null)
    if (user) {
      setProfile(prev => ({
        ...prev,
        nickname: user.nickname || user.display_name || '用户',
        email: user.email || '',
        avatar: user.avatar || '👤',
        birthDate: user.birth_date || '',
        birthTime: user.birth_time || '',
        timezone: user.timezone || 'Asia/Shanghai',
      }))
    }
  }, [])

  // Use useCallback for stable reference (Vercel rule: rerender-functional-setstate)
  const handleProfileChange = useCallback((field: keyof UserProfile, value: string) => {
    setProfile(prev => ({ ...prev, [field]: value }))
  }, [])

  const handleSaveProfile = useCallback(async () => {
    setLoading(true)
    try {
      const user = getLocalStorageJSON<Record<string, string> | null>('user', null)
      const token = getAccessToken()

      if (user?.user_id && token) {
        const res = await fetch(`${API_BASE}/users/me/profile`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`
          },
          body: JSON.stringify({
            display_name: profile.nickname,
            birth_datetime: profile.birthDate && profile.birthTime
              ? `${profile.birthDate}T${profile.birthTime}:00`
              : undefined,
            timezone: profile.timezone,
          }),
        })

        if (res.ok) {
          setLocalStorageJSON('user', { ...user, ...profile })
        }
      }
    } catch (err) {
      console.error('Failed to save profile:', err)
    } finally {
      setLoading(false)
    }
  }, [profile])

  // 设置内容区域
  const SettingsContent = () => {
    switch (activeSection) {
      case 'profile':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">个人资料</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm text-text-secondary mb-2">头像</label>
                <div className="flex items-center gap-4">
                  <div className="w-16 h-16 rounded-full bg-[var(--bg-secondary)] flex items-center justify-center text-3xl">
                    {profile.avatar}
                  </div>
                  <button className="btn btn-secondary">更换头像</button>
                </div>
              </div>
              <div>
                <label className="block text-sm text-text-secondary mb-2">昵称</label>
                <input
                  type="text"
                  value={profile.nickname}
                  onChange={(e) => handleProfileChange('nickname', e.target.value)}
                  className="input"
                />
              </div>
              <div>
                <label className="block text-sm text-text-secondary mb-2">邮箱</label>
                <input
                  type="email"
                  value={profile.email}
                  disabled
                  className="input bg-[var(--bg-secondary)]"
                />
              </div>
              <div>
                <label className="block text-sm text-text-secondary mb-2">出生日期</label>
                <input
                  type="date"
                  value={profile.birthDate}
                  onChange={(e) => handleProfileChange('birthDate', e.target.value)}
                  className="input"
                />
              </div>
              <div>
                <label className="block text-sm text-text-secondary mb-2">出生时间</label>
                <input
                  type="time"
                  value={profile.birthTime}
                  onChange={(e) => handleProfileChange('birthTime', e.target.value)}
                  className="input"
                />
              </div>
              <div>
                <label className="block text-sm text-text-secondary mb-2">时区</label>
                <select
                  value={profile.timezone}
                  onChange={(e) => handleProfileChange('timezone', e.target.value)}
                  className="input"
                >
                  <option value="Asia/Shanghai">Asia/Shanghai (UTC+8)</option>
                  <option value="Asia/Hong_Kong">Asia/Hong_Kong (UTC+8)</option>
                  <option value="Asia/Tokyo">Asia/Tokyo (UTC+9)</option>
                  <option value="America/New_York">America/New_York (UTC-5)</option>
                  <option value="America/Los_Angeles">America/Los_Angeles (UTC-8)</option>
                  <option value="Europe/London">Europe/London (UTC+0)</option>
                </select>
              </div>
              <button onClick={handleSaveProfile} disabled={loading} className="btn btn-primary">
                {loading ? '保存中...' : '保存更改'}
              </button>
            </div>
          </div>
        )
      case 'notifications':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">通知设置</h2>
            <div className="space-y-4">
              {[
                { id: 'daily', label: '每日运势提醒', desc: '每天早上推送今日运势' },
                { id: 'astro', label: '星象事件提醒', desc: '重要星象事件（水逆、满月等）' },
                { id: 'memory', label: '记忆更新提醒', desc: '当 VibeLife 有新的洞察时' },
                { id: 'promo', label: '优惠活动通知', desc: '会员优惠和活动信息' },
              ].map((item) => (
                <div key={item.id} className="flex items-center justify-between p-4 bg-[var(--bg-card-alt)] rounded-lg">
                  <div>
                    <p className="text-text-primary font-medium">{item.label}</p>
                    <p className="text-sm text-text-secondary">{item.desc}</p>
                  </div>
                  <label className="relative inline-flex items-center cursor-pointer">
                    <input type="checkbox" defaultChecked className="sr-only peer" />
                    <div className="w-11 h-6 bg-[var(--bg-secondary)] peer-focus:ring-2 peer-focus:ring-amber rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-accent"></div>
                  </label>
                </div>
              ))}
            </div>
          </div>
        )
      case 'membership':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">会员管理</h2>
            <div className="card p-6">
              <div className="flex items-center justify-between mb-4">
                <div>
                  <p className="text-text-primary font-medium">当前状态</p>
                  <p className="text-sm text-text-secondary">免费用户</p>
                </div>
                <span className="px-3 py-1 bg-[var(--bg-secondary)] rounded-full text-sm text-text-secondary">Free</span>
              </div>
              <button onClick={() => router.push('/membership')} className="btn btn-premium w-full">
                ✨ 升级 Premium
              </button>
            </div>
            <div className="space-y-3">
              <p className="text-sm text-text-secondary">Premium 会员权益：</p>
              <ul className="space-y-2 text-sm text-text-secondary">
                <li className="flex items-center gap-2"><span className="text-success">✓</span> 完整每日运势</li>
                <li className="flex items-center gap-2"><span className="text-success">✓</span> 30次/天 AI对话</li>
                <li className="flex items-center gap-2"><span className="text-success">✓</span> 完整大运/流年分析</li>
                <li className="flex items-center gap-2"><span className="text-success">✓</span> Identity Prism 深度解读</li>
              </ul>
            </div>
          </div>
        )
      case 'memory':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">记忆管理</h2>
            <p className="text-sm text-text-secondary">VibeLife 会记住你分享的信息，以提供更个性化的体验。</p>
            <div className="space-y-3">
              {[
                { content: '你最近工作压力较大', date: '2天前' },
                { content: '关注职业发展方向', date: '1周前' },
                { content: '对感情问题有困扰', date: '2周前' },
              ].map((memory, i) => (
                <div key={i} className="flex items-center justify-between p-4 bg-[var(--bg-callout)] rounded-lg">
                  <div>
                    <p className="text-text-primary">{memory.content}</p>
                    <p className="text-xs text-text-secondary mt-1">{memory.date}</p>
                  </div>
                  <button className="text-sm text-error">删除</button>
                </div>
              ))}
            </div>
            <button className="text-sm text-error">清除所有记忆</button>
          </div>
        )
      case 'about':
        return (
          <div className="space-y-6">
            <h2 className="font-serif text-xl text-text-primary">关于 VibeLife</h2>
            <div className="text-center py-8">
              <span className="text-5xl mb-4 block">🌟</span>
              <h3 className="font-serif text-2xl text-text-primary mb-2">VibeLife</h3>
              <p className="text-text-secondary mb-4">探索内心世界</p>
              <p className="text-sm text-text-secondary">版本 5.0.0</p>
            </div>
            <div className="space-y-2 text-sm text-text-secondary">
              <button className="w-full p-3 text-left hover:bg-[var(--bg-secondary)] rounded-lg">用户协议</button>
              <button className="w-full p-3 text-left hover:bg-[var(--bg-secondary)] rounded-lg">隐私政策</button>
              <button className="w-full p-3 text-left hover:bg-[var(--bg-secondary)] rounded-lg">联系我们</button>
            </div>
          </div>
        )
      default:
        return (
          <div className="text-center py-8 text-text-secondary">
            <p>该功能正在开发中...</p>
          </div>
        )
    }
  }

  // PC端右侧边栏 - 设置菜单
  const AsideContent = () => (
    <div className="space-y-2">
      {SETTINGS_MENU.map((item) => (
        <button
          key={item.id}
          onClick={() => setActiveSection(item.id)}
          className={`w-full flex items-center gap-3 p-3 rounded-lg text-left transition-colors ${
            activeSection === item.id
              ? 'bg-accent/10 text-accent'
              : 'text-text-secondary hover:bg-[var(--bg-secondary)]'
          }`}
        >
          <span className="text-lg">{item.icon}</span>
          <span className="text-sm">{item.label}</span>
        </button>
      ))}
      <div className="pt-4 mt-4 border-t border-[var(--border-light)]">
        <button className="w-full flex items-center gap-3 p-3 rounded-lg text-left text-error hover:bg-error/5 transition-colors">
          <span className="text-lg">🚪</span>
          <span className="text-sm">退出登录</span>
        </button>
      </div>
    </div>
  )

  // PC端主内容
  const PCContent = () => (
    <div className="card p-6">
      <SettingsContent />
    </div>
  )

  // 移动端内容
  const MobileContent = () => (
    <div className="min-h-screen bg-bg-primary lg:hidden">
      <nav className="nav">
        <div className="nav-container">
          <button onClick={() => router.back()} className="text-text-secondary">← 返回</button>
          <h1 className="font-serif text-card-title text-text-primary">设置</h1>
          <div className="w-8" />
        </div>
      </nav>

      <main className="max-w-lg mx-auto px-4 py-6">
        <div className="space-y-2">
          {SETTINGS_MENU.map((item, index) => (
            <button
              key={item.id}
              onClick={() => {
                if (item.id === 'membership') {
                  router.push('/membership')
                } else {
                  setActiveSection(item.id)
                }
              }}
              className="w-full card p-4 flex items-center gap-3 animate-slide-in-up stagger-item"
              style={{ animationDelay: `${index * 50}ms` }}
            >
              <span className="text-xl">{item.icon}</span>
              <span className="text-text-primary">{item.label}</span>
              <span className="ml-auto text-text-secondary">›</span>
            </button>
          ))}
        </div>

        <div className="mt-8">
          <button className="w-full p-4 text-center text-error">退出登录</button>
        </div>

        <div className="mt-8 text-center text-sm text-text-secondary">
          <p>VibeLife v5.0.0</p>
        </div>
      </main>
    </div>
  )

  return (
    <>
      {/* PC端: 使用 PCLayout 三栏布局 */}
      <div className="hidden lg:block">
        <PCLayout
          breadcrumb={[
            { label: '首页', href: '/' },
            { label: '设置' }
          ]}
          aside={<AsideContent />}
          showCommandBar={false}
        >
          <PCContent />
        </PCLayout>
      </div>

      {/* 移动端: 简单单栏布局 */}
      <MobileContent />
    </>
  )
}

