interface AuthCardProps {
  title: string;
  subtitle?: string;
  children: ReactNode;
  footer?: ReactNode;
  className?: string;
}

export const AuthCard = memo(function AuthCard({
  title,
  subtitle,
  children,
  footer,
  className,
}: AuthCardProps) {
  return (
    <div className={cn("w-full", className)}>
      {/* Mobile Logo */}
      <div className="lg:hidden flex flex-col items-center mb-8">
        <div className="w-16 h-16 rounded-2xl bg-gradient-to-br from-mystic-500 to-gold-500 flex items-center justify-center shadow-elevated mb-4">
