// ═══════════════════════════════════════════════════════════════════════════
// AuthLink - 认证页面链接
// ═══════════════════════════════════════════════════════════════════════════

interface AuthLinkProps {
  text: string;
  linkText: string;
  href: string;
  onClick?: () => void;
  className?: string;
}

export const AuthLink = memo(function AuthLink({
  text,
  linkText,
  href,
  onClick,
  className,
}: AuthLinkProps) {
  return (
    <p className={cn("text-sm text-text-secondary", className)}>
      {text}{" "}
      <a
        href={href}
        onClick={(e) => {
          if (onClick) {
            e.preventDefault();
            onClick();
          }
        }}
        className="text-accent-primary font-medium hover:underline"
      >
        {linkText}
      </a>
    </p>
  );
});

