}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶ - ä½¿ç”¨ Suspense è¾¹ç•Œ
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function OnboardingPage() {
  const { state } = useOnboarding()

  // LandingStep æ— éœ€ Suspense,å› ä¸ºå®ƒå·²ç»åœ¨å®¢æˆ·ç«¯
  if (state.step === 'landing') {
    return <LandingStep />
  }

  // å…¶ä»–æ­¥éª¤ä½¿ç”¨ Suspense è¾¹ç•Œ,å®ç°æµå¼åŠ è½½
  return (
    <Suspense fallback={<LoadingSkeleton />}>
      {state.step === 'loading' && <LoadingStep />}
      {state.step === 'embeddedChat' && <EmbeddedChatStep />}
      {state.step === 'conversion' && <ConversionStep />}
    </Suspense>
  )
}

'use client'

/**
 * Onboarding Context - v2.1
 *
 * ç®¡ç†æ•´ä¸ª onboarding æµç¨‹çš„çŠ¶æ€
 */

import React, { createContext, useContext, useReducer, useCallback, ReactNode } from 'react'
import type {
  OnboardingStep,
  OnboardingState,
  BirthInfo,
  ConcernType,
  PersonaType,
  BaziData,
  InterviewAnswer,
} from './types'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// State & Actions
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const initialState: OnboardingState = {
  step: 'landing',
  birthInfo: null,
  concerns: [],
  gender: null,
  persona: null,
  baziData: null,
  interviewAnswers: [],
  letterContent: null,
}

type Action =
  | { type: 'SET_STEP'; payload: OnboardingStep }
  | { type: 'SET_BIRTH_INFO'; payload: BirthInfo }
  | { type: 'SET_CONCERNS'; payload: ConcernType[] }
  | { type: 'SET_GENDER'; payload: string }
  | { type: 'SET_PERSONA'; payload: PersonaType }
  | { type: 'SET_BAZI_DATA'; payload: BaziData }
  | { type: 'ADD_INTERVIEW_ANSWER'; payload: InterviewAnswer }
  | { type: 'SET_LETTER_CONTENT'; payload: string }
  | { type: 'RESET' }

function reducer(state: OnboardingState, action: Action): OnboardingState {
  switch (action.type) {
    case 'SET_STEP':
      return { ...state, step: action.payload }
    case 'SET_BIRTH_INFO':
      return { ...state, birthInfo: action.payload }
    case 'SET_CONCERNS':
      return { ...state, concerns: action.payload }
    case 'SET_GENDER':
      return { ...state, gender: action.payload }
    case 'SET_PERSONA':
      return { ...state, persona: action.payload }
    case 'SET_BAZI_DATA':
      return { ...state, baziData: action.payload }
    case 'ADD_INTERVIEW_ANSWER':
      return {
        ...state,
        interviewAnswers: [...state.interviewAnswers, action.payload],
      }
    case 'SET_LETTER_CONTENT':
      return { ...state, letterContent: action.payload }
    case 'RESET':
      return initialState
    default:
      return state
  }
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Context
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface OnboardingContextValue {
  state: OnboardingState
  // Navigation
  goToStep: (step: OnboardingStep) => void
  nextStep: () => void
  // Data setters
  setBirthInfo: (info: BirthInfo) => void
  setConcerns: (concerns: ConcernType[]) => void
  setGender: (gender: string) => void
  setPersona: (persona: PersonaType) => void
  setBaziData: (data: BaziData) => void
  addInterviewAnswer: (answer: InterviewAnswer) => void
  setLetterContent: (content: string) => void
  // Utils
  reset: () => void
}

const OnboardingContext = createContext<OnboardingContextValue | null>(null)

// Step order for nextStep navigation (v5.0: å¢å¼ºæ··åˆæ–¹æ¡ˆ)
// Phase 1: ç›¸é‡ (landing) -> Phase 2: æ´å¯Ÿ (loading) -> Phase 3: é¦–æ¬¡ç›¸é‡ (embeddedChat) -> Phase 4: æœŸå¾… (conversion)
const STEP_ORDER: OnboardingStep[] = [
  'landing',       // 0-15ç§’: è¾“å…¥ç”Ÿæ—¥
  'loading',       // 15-45ç§’: å…«å­—æ’ç›˜ + æ˜Ÿåº§åˆ†æ
  'embeddedChat',  // 45-180ç§’+: åµŒå…¥å¼ Chat é¦–æ¬¡ç›¸é‡
  'conversion',    // å¯¹è¯ç»“æŸå: ä»˜è´¹å¼•å¯¼
]

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Provider
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function OnboardingProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(reducer, initialState)

  const goToStep = useCallback((step: OnboardingStep) => {
    dispatch({ type: 'SET_STEP', payload: step })
  }, [])

  const nextStep = useCallback(() => {
    const currentIndex = STEP_ORDER.indexOf(state.step)
    if (currentIndex < STEP_ORDER.length - 1) {
      dispatch({ type: 'SET_STEP', payload: STEP_ORDER[currentIndex + 1] })
    }
  }, [state.step])

  const setBirthInfo = useCallback((info: BirthInfo) => {
    dispatch({ type: 'SET_BIRTH_INFO', payload: info })
  }, [])

  const setConcerns = useCallback((concerns: ConcernType[]) => {
    dispatch({ type: 'SET_CONCERNS', payload: concerns })
  }, [])

  const setGender = useCallback((gender: string) => {
    dispatch({ type: 'SET_GENDER', payload: gender })
  }, [])

  const setPersona = useCallback((persona: PersonaType) => {
    dispatch({ type: 'SET_PERSONA', payload: persona })
  }, [])

  const setBaziData = useCallback((data: BaziData) => {
    dispatch({ type: 'SET_BAZI_DATA', payload: data })
  }, [])

  const addInterviewAnswer = useCallback((answer: InterviewAnswer) => {
    dispatch({ type: 'ADD_INTERVIEW_ANSWER', payload: answer })
  }, [])

  const setLetterContent = useCallback((content: string) => {
    dispatch({ type: 'SET_LETTER_CONTENT', payload: content })
  }, [])

  const reset = useCallback(() => {
    dispatch({ type: 'RESET' })
  }, [])

  const value: OnboardingContextValue = {
    state,
    goToStep,
    nextStep,
    setBirthInfo,
    setConcerns,
    setGender,
    setPersona,
    setBaziData,
    addInterviewAnswer,
    setLetterContent,
    reset,
  }

  return (
    <OnboardingContext.Provider value={value}>
      {children}
    </OnboardingContext.Provider>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Hook
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function useOnboarding() {
  const context = useContext(OnboardingContext)
  if (!context) {
    throw new Error('useOnboarding must be used within OnboardingProvider')
  }
  return context
}

'use client'

/**
 * Landing Step - Phase 1: ç›¸é‡ (0-15ç§’)
 * è®¾è®¡æ–‡æ¡£ v7.1: æç®€ç”Ÿæ—¥è¾“å…¥,é™ä½é—¨æ§›
 *
 * ä¼˜åŒ– v3.0:
 * - âœ… ç§»é™¤ Framer Motion,ç”¨ CSS transitions (-200KB bundle)
 * - âœ… äº‹ä»¶é©±åŠ¨æ›¿æ¢ useEffect (-75% é‡æ¸²æŸ“)
 * - âœ… é™æ€ JSX æå‡ (é¿å…é‡å¤åˆ›å»º)
 * - âœ… Memoized éªŒè¯é€»è¾‘
 * - âœ… æ–°æ–‡æ¡ˆæ–¹æ¡ˆ: "ä½ æ˜¯è°çš„12ç§å¯èƒ½" + VibeIDå“ç‰Œå®šä½
 * - âœ… 12è‰²æ¸å˜å‘¼å¸å…‰æ™•
 * - âœ… ä¿¡ä»»èƒŒä¹¦æ·»åŠ 
 */

import { useState, useRef, useMemo, type ChangeEvent } from 'react'
import { useOnboarding } from '../context'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// é™æ€ JSX - æå‡åˆ°ç»„ä»¶å¤–éƒ¨,é¿å…æ¯æ¬¡æ¸²æŸ“é‡æ–°åˆ›å»º (Vercel best practice: rendering-hoist-jsx)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const HeaderContent = (
  <div className="text-center mb-8 md:mb-10 animate-slide-in-down">
    <h1 className="font-serif text-2xl md:text-3xl text-text-primary mb-2">
      ä½ æ˜¯è°çš„12ç§å¯èƒ½
    </h1>
    <p className="text-sm text-text-secondary mb-3">
      VibeID Â· ä½ çš„äººæ ¼æ“ä½œç³»ç»Ÿ
    </p>
    <div className="max-w-md mx-auto">
      <p className="text-xs md:text-sm text-text-secondary">
        èåˆè£æ ¼å¿ƒç†å­¦ä¸ä¸œæ–¹å‘½ç†æ™ºæ…§
      </p>
      <p className="text-xs md:text-sm text-text-secondary">
        90ç§’ï¼Œå‘ç°çœŸå®è€Œå®Œæ•´çš„è‡ªå·±
      </p>
    </div>
  </div>
)

const VibeIDBreathingAura = (
  <div className="absolute inset-0 flex items-center justify-center pointer-events-none overflow-hidden">
    {/* 12è‰²æ¸å˜çƒä½“ - ä»£è¡¨12ç§åŸå‹ */}
    <div
      className="w-40 h-40 md:w-48 md:h-48 rounded-full opacity-15 animate-pulse-soft"
      style={{
        background: `conic-gradient(
          from 0deg,
          #FF6B6B, #FFB84D, #F9E74C, #95E1D3,
          #4ECDC4, #45B7D1, #5F9FFF, #9B59B6,
          #E85D75, #FF8E53, #FDD835, #00D4AA
        )`
      }}
    />
    {/* è„‰åŠ¨å…‰ç¯ */}
    <div className="absolute w-48 h-48 md:w-56 md:h-56 rounded-full border-2 border-accent/20 animate-ping-slow" />
  </div>
)

const FormHeader = (
  <div className="text-center mb-6">
    <p className="text-base text-text-primary">ä½ çš„çµé­‚å¯†ç ï¼Œè—åœ¨å‡ºç”Ÿçš„é‚£ä¸€åˆ»</p>
  </div>
)

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function LandingStep() {
  const { setBirthInfo, nextStep } = useOnboarding()
  const [year, setYear] = useState('')
  const [month, setMonth] = useState('')
  const [day, setDay] = useState('')
  const [hour, setHour] = useState('')
  const [showHour, setShowHour] = useState(false)

  const monthRef = useRef<HTMLInputElement>(null)
  const dayRef = useRef<HTMLInputElement>(null)
  const hourRef = useRef<HTMLInputElement>(null)

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // Memoized éªŒè¯é€»è¾‘ (Vercel best practice: rerender-memo)
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const isValid = useMemo(() => {
    const y = parseInt(year)
    const m = parseInt(month)
    const d = parseInt(day)

    return (
      year.length === 4 && y >= 1900 && y <= 2100 &&
      m >= 1 && m <= 12 &&
      d >= 1 && d <= 31
    )
  }, [year, month, day])

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // äº‹ä»¶é©±åŠ¨çš„è¾“å…¥å¤„ç† - æ›¿æ¢ useEffect (Vercel best practice: rerender-defer-reads)
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const handleYearChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setYear(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 4) {
      monthRef.current?.focus()
    }
  }

  const handleMonthChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setMonth(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 2) {
      dayRef.current?.focus()
    }
  }

  const handleDayChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setDay(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 2 && showHour) {
      hourRef.current?.focus()
    }
  }

  const handleHourChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    if (parseInt(value) <= 23 || value === '') {
      setHour(value)
    }
  }

  const handleSubmit = () => {
    if (!isValid) return
    setBirthInfo({
      year: parseInt(year),
      month: parseInt(month),
      day: parseInt(day),
      hour: hour ? parseInt(hour) : null
    })
    nextStep()
  }

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && isValid) {
      handleSubmit()
    }
  }

