          monthPillar: pillars?.month || { stem: 'ä¸™', branch: 'å¯…' },
          dayPillar: pillars?.day || { stem: 'æˆŠ', branch: 'ç”³' },
          hourPillar: state.birthInfo?.hour ? (pillars?.hour || { stem: 'å£¬', branch: 'å­' }) : null,
          dayMaster: dayMaster?.stem
            ? `${dayMaster.stem}${elementMap[dayMaster.element || ''] || 'åœŸ'}`
            : 'æˆŠåœŸ',
          dayMasterElement: dayMaster?.element || 'åœŸ',
          insight: 'ä½ æ˜¯ä¸€ä¸ªéœ€è¦ç¨³å®šæ„Ÿçš„äººï¼Œå´æ€»è¢«å†…å¿ƒçš„ä¸å®‰é©±åŠ¨ç€å»è¿½æ±‚æ›´å¤šã€‚',
          fortune: { score: 78, luckyColor: 'æ·±è“', luckyNumber: 7, advice: 'ä»Šæ—¥é€‚åˆå¤„ç†é‡è¦å†³ç­–' },
          sunSign: (zodiacResult?.sun_sign_chinese as string) || 'é‡‘ç‰›åº§',
          moonSign: (zodiacResult?.moon_sign_chinese as string) || 'å·¨èŸ¹åº§',
          risingSign: (zodiacResult?.rising_sign_chinese as string) || 'å¤©èåº§',
        })
        goToStep('embeddedChat')
      }, 800)
      return () => clearTimeout(timer)
    }
  }, [done, baziResult, zodiacResult, state.birthInfo?.hour, setBaziData, goToStep])

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4">
      {LoadingHeader}

      {/* æ­¥éª¤åˆ—è¡¨ - ä½¿ç”¨ CSS åŠ¨ç”» */}
      <div className="card w-full md:max-w-[500px] p-4 animate-spring-scale">
        <div className="space-y-2">
          {STEPS.map((step, index) => (
            <StepIndicator
              key={step}
              step={step}
              index={index}
              current={current}
              done={done}
            />
          ))}
        </div>
      </div>

      {/* è¿›åº¦æç¤º */}
      <p className="text-xs text-text-disabled mt-6 animate-fade-in">
        è¯·ç¨å€™,æ­£åœ¨ä¸ºä½ è§£è¯»å‘½ç›˜...
      </p>
    </div>
  )
}

