const INSIGHT_ICONS: Record<string, string> = {
  inner_conflict: 'ğŸ’­',
  external_expression: 'ğŸ­',
  current_state: 'ğŸŒŠ',
};

const INSIGHT_LABELS: Record<string, string> = {
  inner_conflict: 'å†…åœ¨',
