// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const GuidanceCard: React.FC<GuidanceCardProps> = ({
  question = 'æœ€è¿‘æœ‰ä»€ä¹ˆè®©ä½ å›°æ‰°çš„äº‹å—ï¼Ÿ',
  options = DEFAULT_OPTIONS,
  allow_skip = true,
  skip_label = 'æˆ‘åªæ˜¯å¥½å¥‡',
  onSendMessage,
}) => {
  const [selectedOption, setSelectedOption] = useState<string | null>(null);
  const [isAnimating, setIsAnimating] = useState(false);

  // å¤„ç†é€‰é¡¹ç‚¹å‡»
  const handleOptionClick = (option: GuidanceOption) => {
    if (selectedOption) return; // é˜²æ­¢é‡å¤ç‚¹å‡»

    setSelectedOption(option.id);
    setIsAnimating(true);

    // å‘é€é€‰æ‹©æ¶ˆæ¯
    if (onSendMessage) {
      setTimeout(() => {
        onSendMessage(option.label);
      }, 300); // ç­‰å¾…åŠ¨ç”»
    }
  };

  // å¤„ç†è·³è¿‡
  const handleSkip = () => {
    if (selectedOption) return;

    setSelectedOption('skip');
    setIsAnimating(true);

    if (onSendMessage) {
      setTimeout(() => {
        onSendMessage(skip_label);
      }, 300);
    }
  };

  return (
