          <div>
            <p className="text-[var(--text-primary)] font-medium leading-relaxed">
              {question}
            </p>
          </div>
        </div>
      </div>

      {/* Options Grid */}
      <div className="px-4 sm:px-6 py-4 space-y-3">
        {options.map((option, index) => (
          <button
            key={option.id}
            onClick={() => handleOptionClick(option)}
            disabled={Boolean(selectedOption)}
            className={cn(
              "guidance-option w-full p-4 rounded-xl text-left",
              "border border-[var(--border-default)]",
              "transition-all duration-200",
              "hover:bg-[var(--bg-secondary)] hover:border-[var(--border-strong)]",
              "focus:outline-none focus:ring-2 focus:ring-[var(--ring-primary)] focus:ring-offset-2",
              "disabled:opacity-50 disabled:cursor-not-allowed",
              selectedOption === option.id && "bg-[var(--bg-brand-subtle)] border-[var(--border-brand)]"
            )}
            style={{
              animationDelay: `${index * 50}ms`,
            }}
          >
            <div className="flex items-center gap-3">
              <span className="text-2xl flex-shrink-0">
                {option.emoji || 'ğŸ“'}
              </span>
              <div className="flex-1 min-w-0">
                <p className="font-medium text-[var(--text-primary)]">
                  {option.label}
                </p>
                {option.description && (
                  <p className="text-sm text-[var(--text-secondary)] mt-0.5 truncate">
                    {option.description}
                  </p>
                )}
              </div>
              {/* Arrow indicator */}
              <svg
                className={cn(
                  "w-5 h-5 text-[var(--text-tertiary)] flex-shrink-0",
                  "transition-transform duration-200",
                  selectedOption === option.id && "text-[var(--text-brand)] translate-x-1"
                )}
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9 5l7 7-7 7"
                />
              </svg>
            </div>
          </button>
        ))}
      </div>

      {/* Skip Option */}
      {allow_skip && (
        <div className="px-4 sm:px-6 py-3 border-t border-[var(--border-subtle)] bg-[var(--bg-secondary)]/30">
          <button
            onClick={handleSkip}
