.thinking-steps {
  padding: 0 0.875rem 0.75rem 1.75rem;
  margin: 0;
  list-style: none;
}

.thinking-step {
  display: flex;
  align-items: flex-start;
  gap: 0.5rem;
  font-size: var(--text-xs);
  color: var(--text-tertiary);
  line-height: 1.5;
  padding: 0.25rem 0;
}

.thinking-step.active {
  color: var(--text-secondary);
}

.step-dot {
  width: 4px;
  height: 4px;
  margin-top: 0.4rem;
  border-radius: 50%;
  background: var(--accent-primary);
  opacity: 0.6;
  flex-shrink: 0;
  transition: all var(--duration-fast) var(--ease-out-expo);
}

/* Phase-specific dot colors */
.step-dot.analyzing {
  background: var(--accent-primary);
}

.step-dot.skill_activate {
  background: var(--color-info);
}

.step-dot.tool_call {
  background: var(--color-warning);
}

.step-dot.complete {
  background: var(--color-success);
  opacity: 1;
}

.step-content {
  flex: 1;
  min-width: 0;
}

/* Streaming animation for active step */
.thinking-step.active .step-dot {
  animation: pulse-dot 1s ease-in-out infinite;
}

@keyframes pulse-dot {
  0%, 100% {
    opacity: 0.4;
    transform: scale(0.8);
  }
  50% {
    opacity: 1;
    transform: scale(1.2);
  }
}

/* Thinking dots (different from streaming-dots) */
.thinking-dots {
  display: inline-flex;
  align-items: center;
  gap: 2px;
}

.thinking-dots span {
  width: 3px;
  height: 3px;
  border-radius: 50%;
  background: var(--accent-primary);
  opacity: 0.6;
  animation: thinking-bounce 1.4s ease-in-out infinite;
}

.thinking-dots span:nth-child(2) {
  animation-delay: 0.15s;
}

.thinking-dots span:nth-child(3) {
  animation-delay: 0.3s;
}

@keyframes thinking-bounce {
  0%, 80%, 100% {
    opacity: 0.3;
    transform: scale(0.8);
  }
  40% {
    opacity: 1;
    transform: scale(1);
  }
}

/* Dark mode adjustments */
[data-theme="dark"] .thinking-block,
.dark .thinking-block {
  background: var(--bg-secondary);
  border-color: var(--border-default);
}

[data-theme="dark"] .thinking-block.streaming,
.dark .thinking-block.streaming {
  border-color: var(--accent-primary);
  box-shadow: 0 0 0 1px rgba(234, 190, 138, 0.2);
}

[data-theme="dark"] .thinking-header:hover,
.dark .thinking-header:hover {
  background: rgba(255, 255, 255, 0.05);
}

/* PCç«¯ä¼˜åŒ– */
@media (min-width: 1024px) {
  .thinking-block {
    border-radius: var(--radius-xl);
  }

  .thinking-header {
    padding: 0.75rem 1rem;
    font-size: var(--text-base);
  }

  .thinking-steps {
    padding: 0 1rem 0.875rem 2rem;
  }

  .thinking-step {
    font-size: var(--text-sm);
    padding: 0.3rem 0;
  }

  .step-dot {
    width: 5px;
    height: 5px;
    margin-top: 0.45rem;
  }
}

