'use client'

/**
 * Onboarding Page - v6.0
 *
 * å¤šå˜ä½“å…¥å£ï¼šbazi / zodiac / jungastro / dankoe
 *
 * URL å‚æ•°ï¼š
 * - /onboarding              â†’ é»˜è®¤ bazi
 * - /onboarding?variant=zodiac
 * - /onboarding?variant=dankoe
 *
 * ä¼˜åŒ–:
 * - âœ… åŠ¨æ€å¯¼å…¥æ­¥éª¤ç»„ä»¶ (Vercel best practice: bundle-dynamic-imports)
 * - âœ… Suspense è¾¹ç•Œå®ç°æµå¼åŠ è½½ (Vercel best practice: async-suspense-boundaries)
 * - âœ… å˜ä½“é©±åŠ¨çš„å·®å¼‚åŒ–ä½“éªŒ
 */

import { Suspense } from 'react'
import dynamic from 'next/dynamic'
import { useSearchParams } from 'next/navigation'
import { useOnboarding } from './context'
import type { VariantId } from './types'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// åŠ¨æ€å¯¼å…¥æ­¥éª¤ç»„ä»¶ - å‡å°‘é¦–å± bundle (Vercel best practice: bundle-dynamic-imports)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

// LandingStep ä¸åŠ¨æ€å¯¼å…¥,å› ä¸ºå®ƒæ˜¯é¦–å±å¿…éœ€
import LandingStep from './steps/LandingStep'

// å…¶ä»–æ­¥éª¤åŠ¨æ€å¯¼å…¥,åªåœ¨éœ€è¦æ—¶åŠ è½½
const LoadingStep = dynamic(() => import('./steps/LoadingStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const EmbeddedChatStep = dynamic(() => import('./steps/EmbeddedChatStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

const ConversionStep = dynamic(() => import('./steps/ConversionStep'), {
  loading: () => <LoadingSkeleton />,
  ssr: false
})

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Loading Skeleton - Suspense fallback
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function LoadingSkeleton() {
  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="flex flex-col items-center gap-4">
        <div className="w-16 h-16 rounded-full border-4 border-accent-primary/20 border-t-accent-primary animate-spin" />
        <p className="text-sm text-text-secondary">åŠ è½½ä¸­...</p>
      </div>
    </div>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶ - ä½¿ç”¨ Suspense è¾¹ç•Œ
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function OnboardingFlow() {
  const { state } = useOnboarding()

  // dankoe å˜ä½“ï¼šè·³è¿‡ landing å’Œ loadingï¼Œç›´æ¥è¿›å…¥ embeddedChat
  // å…¶ä»–å˜ä½“ï¼šä» landing å¼€å§‹

  // LandingStep æ— éœ€ Suspense,å› ä¸ºå®ƒå·²ç»åœ¨å®¢æˆ·ç«¯
  if (state.step === 'landing') {
    return <LandingStep />
  }

  // å…¶ä»–æ­¥éª¤ä½¿ç”¨ Suspense è¾¹ç•Œ,å®ç°æµå¼åŠ è½½
  return (
    <Suspense fallback={<LoadingSkeleton />}>
      {state.step === 'loading' && <LoadingStep />}
      {state.step === 'embeddedChat' && <EmbeddedChatStep />}
      {state.step === 'conversion' && <ConversionStep />}
    </Suspense>
  )
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// é¡µé¢å…¥å£ - è¯»å– variant å‚æ•°
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function OnboardingPage() {
  const searchParams = useSearchParams()
  const variantParam = searchParams.get('variant') as VariantId | null

  // éªŒè¯ variant å‚æ•°ï¼Œæ— æ•ˆåˆ™ä½¿ç”¨é»˜è®¤å€¼
  const validVariants: VariantId[] = ['bazi', 'zodiac', 'jungastro', 'dankoe']
  const variant: VariantId = variantParam && validVariants.includes(variantParam)
    ? variantParam
    : 'bazi'

  // æ³¨æ„ï¼šOnboardingProvider åœ¨ layout.tsx ä¸­ï¼Œè¿™é‡Œåªæ˜¯ Flow
  // å¦‚æœéœ€è¦åœ¨è¿™é‡Œä¼ é€’ variantï¼Œéœ€è¦é‡æ„ layout
  return <OnboardingFlow />
}

'use client'

/**
 * Landing Step - Phase 1: ç›¸é‡ (0-15ç§’)
 *
 * v4.0 å¤šå˜ä½“æ”¯æŒ:
 * - bazi: å…«å­—å‘½ç†å…¥å£
 * - zodiac: æ˜Ÿåº§å æ˜Ÿå…¥å£
 * - jungastro: è£æ ¼å æ˜Ÿå…¥å£
 * - dankoe: äººç”Ÿé‡ç½®å…¥å£ï¼ˆè·³è¿‡æ­¤æ­¥éª¤ï¼‰
 *
 * ä¼˜åŒ–:
 * - âœ… ç§»é™¤ Framer Motion,ç”¨ CSS transitions (-200KB bundle)
 * - âœ… äº‹ä»¶é©±åŠ¨æ›¿æ¢ useEffect (-75% é‡æ¸²æŸ“)
 * - âœ… Memoized éªŒè¯é€»è¾‘
 * - âœ… å˜ä½“é©±åŠ¨çš„æ–‡æ¡ˆå’Œæ ·å¼
 */

import { useState, useRef, useMemo, type ChangeEvent } from 'react'
import { useOnboarding } from '../context'
import { VARIANT_DEFAULTS, VARIANT_THEMES } from '../types'

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// å˜ä½“ç‰¹å®šå†…å®¹
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const VARIANT_SUBTEXTS = {
  bazi: {
    line1: 'èåˆè£æ ¼å¿ƒç†å­¦ä¸ä¸œæ–¹å‘½ç†æ™ºæ…§',
    line2: '90ç§’ï¼Œå‘ç°çœŸå®è€Œå®Œæ•´çš„è‡ªå·±',
    formHeader: 'ä½ çš„çµé­‚å¯†ç ï¼Œè—åœ¨å‡ºç”Ÿçš„é‚£ä¸€åˆ»',
  },
  zodiac: {
    line1: 'å¤ªé˜³å®šæ€§æ ¼ï¼Œæœˆäº®è—æƒ…æ„Ÿï¼Œä¸Šå‡æ˜¾æ°”è´¨',
    line2: '90ç§’ï¼Œè§£è¯»ä½ çš„ä¸‰é‡æ˜Ÿåº§å¯†ç ',
    formHeader: 'æ˜Ÿè±¡èƒ½é‡ï¼Œä»ä½ è¯ç”Ÿçš„é‚£ä¸€åˆ»å¼€å§‹æµåŠ¨',
  },
  jungastro: {
    line1: 'é˜´å½±æ˜¯æœªè¢«æ¥çº³çš„è‡ªæˆ‘',
    line2: '90ç§’ï¼Œå¼€å¯ä½ çš„ä¸ªä½“åŒ–ä¹‹æ—…',
    formHeader: 'ä½ çš„æ˜Ÿç›˜ï¼Œæ˜¯çµé­‚çš„åœ°å›¾',
  },
  dankoe: {
    line1: '', // dankoe ä¸æ˜¾ç¤º landing
    line2: '',
    formHeader: '',
  },
}

// å˜ä½“ç‰¹å®šçš„æ¸å˜è‰²
const VARIANT_GRADIENTS = {
  bazi: `conic-gradient(
    from 0deg,
    #8B4513, #A0522D, #CD853F, #DEB887,
    #D2691E, #BC8F8F, #8B4513, #A0522D,
    #CD853F, #DEB887, #D2691E, #BC8F8F
  )`,
  zodiac: `conic-gradient(
    from 0deg,
    #FF6B6B, #FFB84D, #F9E74C, #95E1D3,
    #4ECDC4, #45B7D1, #5F9FFF, #9B59B6,
    #E85D75, #FF8E53, #FDD835, #00D4AA
  )`,
  jungastro: `conic-gradient(
    from 0deg,
    #2C3E50, #34495E, #5D6D7E, #85929E,
    #1A1A2E, #16213E, #0F3460, #533483,
    #2C3E50, #34495E, #5D6D7E, #85929E
  )`,
  dankoe: `conic-gradient(
    from 0deg,
    #FF6B6B, #FF8E53, #FFC75F, #F9F871,
    #FF6B6B, #FF8E53, #FFC75F, #F9F871,
    #FF6B6B, #FF8E53, #FFC75F, #F9F871
  )`,
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ä¸»ç»„ä»¶
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function LandingStep() {
  const { state, setBirthInfo, nextStep } = useOnboarding()
  const { variant } = state
  const [year, setYear] = useState('')
  const [month, setMonth] = useState('')
  const [day, setDay] = useState('')
  const [hour, setHour] = useState('')
  const [showHour, setShowHour] = useState(false)

  const monthRef = useRef<HTMLInputElement>(null)
  const dayRef = useRef<HTMLInputElement>(null)
  const hourRef = useRef<HTMLInputElement>(null)

  // è·å–å˜ä½“ç‰¹å®šå†…å®¹
  const landing = VARIANT_DEFAULTS[variant]
  const subtext = VARIANT_SUBTEXTS[variant]
  const theme = VARIANT_THEMES[variant]
  const gradient = VARIANT_GRADIENTS[variant]

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // Memoized éªŒè¯é€»è¾‘ (Vercel best practice: rerender-memo)
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const isValid = useMemo(() => {
    const y = parseInt(year)
    const m = parseInt(month)
    const d = parseInt(day)

    return (
      year.length === 4 && y >= 1900 && y <= 2100 &&
      m >= 1 && m <= 12 &&
      d >= 1 && d <= 31
    )
  }, [year, month, day])

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // äº‹ä»¶é©±åŠ¨çš„è¾“å…¥å¤„ç† - æ›¿æ¢ useEffect (Vercel best practice: rerender-defer-reads)
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const handleYearChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setYear(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 4) {
      monthRef.current?.focus()
    }
  }

  const handleMonthChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setMonth(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 2) {
      dayRef.current?.focus()
    }
  }

  const handleDayChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    setDay(value)
    // äº‹ä»¶é©±åŠ¨èšç„¦,æ— éœ€ useEffect
    if (value.length === 2 && showHour) {
      hourRef.current?.focus()
    }
  }

  const handleHourChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.replace(/\D/g, '')
    if (parseInt(value) <= 23 || value === '') {
      setHour(value)
    }
  }

  const handleSubmit = () => {
    if (!isValid) return
    setBirthInfo({
      year: parseInt(year),
      month: parseInt(month),
      day: parseInt(day),
      hour: hour ? parseInt(hour) : null
    })
    nextStep()
  }

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && isValid) {
      handleSubmit()
    }
  }

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4 relative">
      {/* å‘¼å¸å…‰æ™•åŠ¨ç”» - å˜ä½“ç‰¹å®šæ¸å˜ */}
      <div className="absolute inset-0 flex items-center justify-center pointer-events-none overflow-hidden">
        <div
          className="w-40 h-40 md:w-48 md:h-48 rounded-full opacity-15 animate-pulse-soft"
          style={{ background: gradient }}
        />
        <div className="absolute w-48 h-48 md:w-56 md:h-56 rounded-full border-2 border-accent/20 animate-ping-slow" />
      </div>

      {/* Header - å˜ä½“ç‰¹å®šæ–‡æ¡ˆ */}
      <div className="text-center mb-8 md:mb-10 animate-slide-in-down">
        <h1 className="font-serif text-2xl md:text-3xl text-text-primary mb-2">
          {landing.headline}
        </h1>
        <p className="text-sm text-text-secondary mb-3">
          {landing.subheadline}
        </p>
        <div className="max-w-md mx-auto">
          <p className="text-xs md:text-sm text-text-secondary">
            {subtext.line1}
          </p>
          <p className="text-xs md:text-sm text-text-secondary">
            {subtext.line2}
          </p>
        </div>
      </div>

      {/* è¡¨å•å¡ç‰‡ */}
      <div className="card w-full md:max-w-[600px] animate-spring-scale" style={{ animationDelay: '100ms' }}>
        {/* å˜ä½“ç‰¹å®šçš„è¡¨å•å¤´éƒ¨ */}
        <div className="text-center mb-6">
          <p className="text-base text-text-primary">{subtext.formHeader}</p>
        </div>

        <div className="flex items-center gap-2 mb-4">
          <div className="flex-1">
            <input
              type="text"
              inputMode="numeric"
              maxLength={4}
              placeholder="1990"
              value={year}
              onChange={handleYearChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              autoFocus
              aria-label="å‡ºç”Ÿå¹´ä»½"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">å¹´</span>
          </div>
          <div className="w-16">
            <input
              ref={monthRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="5"
              value={month}
              onChange={handleMonthChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              aria-label="å‡ºç”Ÿæœˆä»½"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">æœˆ</span>
          </div>
          <div className="w-16">
            <input
              ref={dayRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="15"
              value={day}
              onChange={handleDayChange}
              onKeyDown={handleKeyDown}
              className="input text-center text-lg font-medium transition-all duration-normal"
              aria-label="å‡ºç”Ÿæ—¥æœŸ"
            />
            <span className="block text-xs text-text-secondary text-center mt-1">æ—¥</span>
          </div>
        </div>

        {!showHour ? (
          <button
            onClick={() => setShowHour(true)}
            className="w-full text-sm text-text-secondary hover:text-accent mb-6 transition-colors duration-normal"
          >
            + æ·»åŠ å‡ºç”Ÿæ—¶é—´ï¼ˆå¯é€‰ï¼‰
          </button>
        ) : (
          <div className="mb-6 text-center animate-fade-in">
            <input
              ref={hourRef}
              type="text"
              inputMode="numeric"
              maxLength={2}
              placeholder="08"
              value={hour}
              onChange={handleHourChange}
              onKeyDown={handleKeyDown}
              className="input w-20 text-center text-lg font-medium transition-all duration-normal"
              aria-label="å‡ºç”Ÿæ—¶è¾°"
            />
            <span className="block text-xs text-text-secondary mt-1">æ—¶ï¼ˆ0-23ï¼‰Â· ä¸çŸ¥é“ä¹Ÿæ²¡å…³ç³»</span>
          </div>
        )}

        <button
          onClick={handleSubmit}
          disabled={!isValid}
          className={`w-full transition-all duration-normal ${
            isValid
              ? 'btn-primary hover:scale-[1.02] active:scale-[0.98]'
              : 'btn bg-bg-secondary text-text-disabled cursor-not-allowed'
          }`}
        >
          {landing.cta}
        </button>
      </div>
    </div>
  )
}

/**
 * Onboarding Flow Types - v3.0
 *
 * ç”¨æˆ·æµç¨‹é˜¶æ®µå®šä¹‰å’ŒçŠ¶æ€ç±»å‹
 * æ”¯æŒå¤šå˜ä½“å…¥å£ï¼šbazi / zodiac / jungastro / dankoe
 */

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// å˜ä½“ç±»å‹
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export type VariantId = 'bazi' | 'zodiac' | 'jungastro' | 'dankoe'

export interface VariantLanding {
  headline: string
  subheadline: string
  cta: string
  skip_birth_info: boolean
}

export interface VariantConfig {
  id: string
  name: string
  description: string
  requires: Array<{ skill: string; compute: boolean }>
