   107â†’// Generate a deterministic pattern from birth data
   108â†’function generateSealPattern(year: number, month: number, day: number): string {
   109â†’  const seed = year * 10000 + month * 100 + day;
   110â†’  const chars = "ABCDEFGHJKLMNPQRSTUVWXYZ";
   111â†’  let result = "";
   112â†’  let n = seed;
   113â†’  for (let i = 0; i < 2; i++) {
   114â†’    result += chars[n % chars.length];
   115â†’    n = Math.floor(n / chars.length);
   116â†’  }
   117â†’  return result;
   118â†’}
