    75â†’          <InsightContent content={content} />
    76â†’        )}
    77â†’      </div>
    78â†’
    79â†’      {/* Action Button */}
    80â†’      <button
    81â†’        className="w-full py-2 px-3 rounded-lg text-sm font-medium flex items-center justify-center gap-1 transition-colors"
    82â†’        style={{
    83â†’          background: 'var(--bg-secondary)',
    84â†’          color: 'var(--text-secondary)',
    85â†’        }}
    86â†’      >
