    92â†’});
    93â†’
    94â†’function InsightContent({ content }: { content: SkillCardContent }) {
    95â†’  const { headline, insights } = content;
    96â†’
    97â†’  return (
    98â†’    <div className="space-y-2">
    99â†’      {/* Headline */}
   100â†’      <p className="font-medium" style={{ color: 'var(--text-primary)' }}>{headline}</p>
   101â†’
   102â†’      {/* Insights */}
   103â†’      {insights.map((insight, index) => (
   104â†’        <p key={index} className="text-sm" style={{ color: 'var(--text-secondary)' }}>
   105â†’          {insight}
   106â†’        </p>
   107â†’      ))}
   108â†’    </div>
   109â†’  );
   110â†’}
   111â†’
   112â†’function RatingContent({ content }: { content: SkillCardContent }) {
   113â†’  const { rating, suggestions } = content;
   114â†’
   115â†’  if (!rating) return null;
   116â†’
   117â†’  // Render stars based on rating (1-5)
   118â†’  const renderStars = (value: number) => {
   119â†’    const filled = Math.round(value);
   120â†’    return (
   121â†’      <span style={{ color: 'var(--gold-500)' }}>
   122â†’        {"â˜…".repeat(filled)}
   123â†’        {"â˜†".repeat(5 - filled)}
   124â†’      </span>
   125â†’    );
   126â†’  };
   127â†’
   128â†’  return (
   129â†’    <div className="space-y-2">
   130â†’      {/* Overall Rating */}
   131â†’      <div className="flex items-center gap-2">
   132â†’        <span className="text-sm" style={{ color: 'var(--text-tertiary)' }}>æ•´ä½“:</span>
   133â†’        {renderStars(rating.overall)}
   134â†’        <span className="text-sm font-medium" style={{ color: 'var(--text-secondary)' }}>{rating.label}</span>
   135â†’      </div>
   136â†’
   137â†’      {/* Detail Ratings */}
   138â†’      {rating.details && (
   139â†’        <div className="space-y-1 pt-2" style={{ borderTop: '1px solid var(--border-subtle)' }}>
   140â†’          {Object.entries(rating.details).map(([key, value]) => (
   141â†’            <div key={key} className="flex items-center gap-2 text-sm">
   142â†’              <span className="w-12" style={{ color: 'var(--text-tertiary)' }}>{key}:</span>
   143â†’              {renderStars(value)}
   144â†’            </div>
   145â†’          ))}
   146â†’        </div>
   147â†’      )}
   148â†’
   149â†’      {/* Suggestions */}
   150â†’      {suggestions && (
   151â†’        <div className="pt-2 space-y-1" style={{ borderTop: '1px solid var(--border-subtle)' }}>
   152â†’          {suggestions.do.length > 0 && (
   153â†’            <p className="text-sm" style={{ color: 'var(--color-success)' }}>
   154â†’              å®œ: {suggestions.do.join("ã€")}
   155â†’            </p>
   156â†’          )}
   157â†’          {suggestions.avoid.length > 0 && (
   158â†’            <p className="text-sm" style={{ color: 'var(--color-error)' }}>
   159â†’              å¿Œ: {suggestions.avoid.join("ã€")}
   160â†’            </p>
   161â†’          )}
   162â†’        </div>
   163â†’      )}
   164â†’    </div>
   165â†’  );
   166â†’}
   167â†’
   168â†’export default SkillCard;
   169â†’

