    33â†’// è§£æå†…å®¹ï¼Œæå– [[TOOL:...]] æ ‡è®°
    34â†’function parseContent(content: string): ContentSegment[] {
    35â†’  const regex = /\[\[TOOL:(\w+):([\s\S]*?)\]\]/g;
    36â†’  const segments: ContentSegment[] = [];
    37â†’  let lastIndex = 0;
    38â†’
    39â†’  const matches = Array.from(content.matchAll(regex));
    40â†’  for (const match of matches) {
    41â†’    // å‰é¢çš„æ–‡æœ¬
    42â†’    if (match.index !== undefined && match.index > lastIndex) {
    43â†’      const text = content.slice(lastIndex, match.index).trim();
    44â†’      if (text) {
    45â†’        segments.push({ type: 'text', content: text });
    46â†’      }
    47â†’    }
    48â†’    // tool å—
    49â†’    try {
    50â†’      segments.push({
    51â†’        type: 'tool',
    52â†’        name: match[1],
    53â†’        data: JSON.parse(match[2])
    54â†’      });
    55â†’    } catch {
    56â†’      // JSON è§£æå¤±è´¥ï¼Œä½œä¸ºæ–‡æœ¬å¤„ç†
    57â†’      segments.push({ type: 'text', content: match[0] });
    58â†’    }
    59â†’    lastIndex = (match.index ?? 0) + match[0].length;
    60â†’  }
    61â†’
    62â†’  // å‰©ä½™æ–‡æœ¬
    63â†’  if (lastIndex < content.length) {
    64â†’    const text = content.slice(lastIndex).trim();
    65â†’    if (text) {
    66â†’      segments.push({ type: 'text', content: text });
    67â†’    }
    68â†’  }
    69â†’
    70â†’  return segments;
    71â†’}
