  { id: 'birth', label: 'ç”Ÿæ—¥ä¿¡æ¯', status: 'done' },      // Pre-completed âœ“
  { id: 'intent', label: 'å¼€å¯å‘½ç›˜', status: 'done' },     // Pre-completed âœ“
  { id: 'bazi', label: 'è§£è¯»å…«å­—', status: 'pending' },
