/* Goal-Gradient progress bar glow */
@keyframes progress-glow {
  0%, 100% { box-shadow: 0 0 8px rgba(234, 190, 138, 0.3); }
  50% { box-shadow: 0 0 16px rgba(234, 190, 138, 0.6); }
}

.animate-progress-glow {
  animation: progress-glow 2s ease-in-out infinite;
}

/* Checklist item completion pop */
@keyframes check-pop {
  0% { transform: scale(0.8); opacity: 0; }
  50% { transform: scale(1.2); }
  100% { transform: scale(1); opacity: 1; }
}

.animate-check-pop {
  animation: check-pop 0.3s var(--ease-spring);
}

/* Slow ping for background rings */
@keyframes ping-slow {
  0% { transform: scale(1); opacity: 0.5; }
  75%, 100% { transform: scale(1.5); opacity: 0; }
}

.animate-ping-slow {
  animation: ping-slow 3s cubic-bezier(0, 0, 0.2, 1) infinite;
}

/* Countdown urgency pulse */
@keyframes urgency-pulse {
  0%, 100% { color: var(--color-warning); }
  50% { color: var(--color-error); }
}

.animate-urgency {
  animation: urgency-pulse 2s ease-in-out infinite;
}
