          if (data.success && data.chart) {
            setBaziResult(data.chart)
          }
        }
      } catch (err) {
        console.error('Bazi API error:', err)
      }
      setDone(prev => [...prev, 0])

      // Step 2: æ˜Ÿåº§
      setCurrent(1)
      try {
        const zodiacRes = await fetch(`${API_BASE}/zodiac/chart`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ birth_date: birthDate, birth_time: birthTime, birth_place: 'Shanghai' })
        })
        if (zodiacRes.ok) {
          const data = await zodiacRes.json()
          if (data.success && data.chart) {
            setZodiacResult(data.chart)
          }
        }
      } catch (err) {
        console.error('Zodiac API error:', err)
      }
      setDone(prev => [...prev, 1])

      // Step 3: èåˆ
      setCurrent(2)
      await new Promise(resolve => setTimeout(resolve, 1500))
      setDone(prev => [...prev, 2])
    }

    fetchData()
  }, [birthParams, goToStep])

  // å®Œæˆåè·³è½¬
  useEffect(() => {
    if (done.length === 3) {
      const timer = setTimeout(() => {
        const pillars = baziResult?.four_pillars as Record<string, { stem: string; branch: string }> | undefined
        const dayMaster = baziResult?.day_master as { stem?: string; element?: string } | undefined

        const elementMap: Record<string, string> = {
          wood: 'æœ¨', fire: 'ç«', earth: 'åœŸ', metal: 'é‡‘', water: 'æ°´'
        }

        setBaziData({
          yearPillar: pillars?.year || { stem: 'ç”²', branch: 'åˆ' },
          monthPillar: pillars?.month || { stem: 'ä¸™', branch: 'å¯…' },
          dayPillar: pillars?.day || { stem: 'æˆŠ', branch: 'ç”³' },
          hourPillar: state.birthInfo?.hour ? (pillars?.hour || { stem: 'å£¬', branch: 'å­' }) : null,
          dayMaster: dayMaster?.stem
            ? `${dayMaster.stem}${elementMap[dayMaster.element || ''] || 'åœŸ'}`
            : 'æˆŠåœŸ',
          dayMasterElement: dayMaster?.element || 'åœŸ',
          insight: 'ä½ æ˜¯ä¸€ä¸ªéœ€è¦ç¨³å®šæ„Ÿçš„äººï¼Œå´æ€»è¢«å†…å¿ƒçš„ä¸å®‰é©±åŠ¨ç€å»è¿½æ±‚æ›´å¤šã€‚',
          fortune: { score: 78, luckyColor: 'æ·±è“', luckyNumber: 7, advice: 'ä»Šæ—¥é€‚åˆå¤„ç†é‡è¦å†³ç­–' },
          sunSign: (zodiacResult?.sun_sign_chinese as string) || 'é‡‘ç‰›åº§',
          moonSign: (zodiacResult?.moon_sign_chinese as string) || 'å·¨èŸ¹åº§',
          risingSign: (zodiacResult?.rising_sign_chinese as string) || 'å¤©èåº§',
        })
        goToStep('embeddedChat')
      }, 800)
      return () => clearTimeout(timer)
    }
  }, [done, baziResult, zodiacResult, state.birthInfo?.hour, setBaziData, goToStep])

  return (
    <div className="min-h-screen bg-bg-primary flex flex-col items-center justify-center px-4">
      {LoadingHeader}

      {/* æ­¥éª¤åˆ—è¡¨ - ä½¿ç”¨ CSS åŠ¨ç”» */}
