        <div className="space-y-2">
          {STEPS.map((step, index) => (
            <StepIndicator
              key={step}
              step={step}
              index={index}
              current={current}
              done={done}
            />
          ))}
        </div>
      </div>

      {/* è¿›åº¦æç¤º */}
      <p className="text-xs text-text-disabled mt-6 animate-fade-in">
        è¯·ç¨å€™,æ­£åœ¨ä¸ºä½ è§£è¯»å‘½ç›˜...
      </p>
    </div>
  )
}

