    57â†’
    58â†’  // è·å–å˜ä½“ä¸»é¢˜
    59â†’  const theme = useMemo(() => VARIANT_THEMES[variant], [variant]);
    60â†’
    61â†’  // å¤„ç†å¯¹è¯å¼€å§‹
    62â†’  const handleConversationStart = useCallback((id: string) => {
    63â†’    setConversationId(id);
    64â†’  }, []);
    65â†’
    66â†’  // åˆå§‹æ¶ˆæ¯ - æ ¹æ®å˜ä½“ä¸åŒ
    67â†’  // dankoe: ç›´æ¥å¼€å§‹å¯¹è¯
    68â†’  // å…¶ä»–: è§¦å‘ onboarding skill
    69â†’  const initialPrompt = useMemo(() => {
    70â†’    if (hasStartedRef.current) return undefined;
    71â†’    return variant === 'dankoe' ? 'æˆ‘å‡†å¤‡å¥½äº†' : 'å¼€å§‹';
    72â†’  }, [variant]);
    73â†’
    74â†’  useEffect(() => {
    75â†’    hasStartedRef.current = true;
    76â†’  }, []);
    77â†’
    78â†’  // ç›‘å¬ localStorage ä¸­çš„ onboarding_completed æ ‡è®°
    79â†’  // åç«¯åœ¨æ‰¿è¯ºæ¶ˆæ¯åä¼šè®¾ç½®æ­¤æ ‡è®°
    80â†’  useEffect(() => {
    81â†’    const checkComplete = () => {
    82â†’      if (completionDetectedRef.current) return;
    83â†’
    84â†’      const completed = localStorage.getItem('vibelife_onboarding_chat_completed');
    85â†’      if (completed === 'true') {
    86â†’        completionDetectedRef.current = true;
    87â†’        localStorage.removeItem('vibelife_onboarding_chat_completed');
    88â†’        // å»¶è¿Ÿè·³è½¬ï¼Œè®©ç”¨æˆ·çœ‹åˆ°æœ€åçš„æ‰¿è¯ºæ¶ˆæ¯
    89â†’        setTimeout(() => nextStep(), 2000);
    90â†’      }
    91â†’    };
    92â†’
    93â†’    // æ¯ç§’æ£€æŸ¥ä¸€æ¬¡
    94â†’    const interval = setInterval(checkComplete, 1000);
    95â†’    return () => clearInterval(interval);
    96â†’  }, [nextStep]);
    97â†’
    98â†’  // æ£€æµ‹ DOM ä¸­çš„æ¶ˆæ¯å†…å®¹æ˜¯å¦åŒ…å«å®Œæˆå…³é”®è¯
    99â†’  // è¿™æ˜¯ä¸€ä¸ªå¤‡ç”¨æ£€æµ‹æœºåˆ¶
   100â†’  useEffect(() => {
   101â†’    const checkMessageContent = () => {
   102â†’      if (completionDetectedRef.current) return;
   103â†’
   104â†’      // æŸ¥æ‰¾æœ€åä¸€æ¡ assistant æ¶ˆæ¯
   105â†’      const messages = document.querySelectorAll('[data-role="assistant"]');
   106â†’      if (messages.length === 0) return;
   107â†’
   108â†’      const lastMessage = messages[messages.length - 1];
   109â†’      const content = lastMessage?.textContent || '';
   110â†’
   111â†’      // æ£€æŸ¥æ˜¯å¦åŒ…å«å®Œæˆå…³é”®è¯
   112â†’      const hasCompletionKeyword = COMPLETION_KEYWORDS.some(keyword =>
   113â†’        content.includes(keyword)
   114â†’      );
   115â†’
   116â†’      if (hasCompletionKeyword) {
   117â†’        completionDetectedRef.current = true;
   118â†’        // è®¾ç½® localStorage æ ‡è®°
   119â†’        localStorage.setItem('vibelife_onboarding_chat_completed', 'true');
   120â†’        // å»¶è¿Ÿè·³è½¬
   121â†’        setTimeout(() => nextStep(), 3000);
   122â†’      }
   123â†’    };
   124â†’
   125â†’    // ä½¿ç”¨ MutationObserver ç›‘å¬æ¶ˆæ¯å˜åŒ–
   126â†’    const observer = new MutationObserver(checkMessageContent);
   127â†’    const chatContainer = document.querySelector('[role="log"]');
   128â†’
   129â†’    if (chatContainer) {
   130â†’      observer.observe(chatContainer, {
   131â†’        childList: true,
   132â†’        subtree: true,
   133â†’        characterData: true,
   134â†’      });
   135â†’    }
   136â†’
   137â†’    return () => observer.disconnect();
   138â†’  }, [nextStep]);
   139â†’
   140â†’  // dankoe ä¸éœ€è¦å‘½ç›˜æ•°æ®ï¼Œå…¶ä»–å˜ä½“éœ€è¦
   141â†’  const needsBaziData = variant !== 'dankoe';
   142â†’  if (needsBaziData && !baziData) {
   143â†’    return (
   144â†’      <LuminousPaper
