    12â†’import { memo, useMemo } from "react";
    13â†’import { cn } from "@/lib/utils";
    14â†’
    15â†’export interface ProtocolProgressProps {
    16â†’  currentStep: number;
    17â†’  totalSteps: number;
    18â†’  phaseName?: string;
    19â†’  className?: string;
    20â†’}
    21â†’
    22â†’export const ProtocolProgress = memo(function ProtocolProgress({
    23â†’  currentStep,
    24â†’  totalSteps,
    25â†’  phaseName,
    26â†’  className,
    27â†’}: ProtocolProgressProps) {
    28â†’  const progress = useMemo(() => {
    29â†’    return Math.min(100, Math.max(0, (currentStep / totalSteps) * 100));
    30â†’  }, [currentStep, totalSteps]);
    31â†’
    32â†’  return (
    33â†’    <div className={cn("w-full", className)}>
    34â†’      {/* Header: Phase name + Step counter */}
    35â†’      <div className="flex items-center justify-between mb-2">
    36â†’        {phaseName && (
    37â†’          <span className="text-xs font-medium text-muted-foreground">
    38â†’            {phaseName}
    39â†’          </span>
    40â†’        )}
    41â†’        <span className="text-xs font-medium text-foreground">
    42â†’          {currentStep} / {totalSteps}
    43â†’        </span>
    44â†’      </div>
    45â†’
    46â†’      {/* Progress bar */}
    47â†’      <div className="relative h-1.5 bg-muted rounded-full overflow-hidden">
    48â†’        <div
    49â†’          className="absolute inset-y-0 left-0 bg-gradient-to-r from-emerald-500 to-teal-500 rounded-full transition-all duration-500 ease-out"
    50â†’          style={{ width: `${progress}%` }}
    51â†’        />
    52â†’        {/* Shimmer effect */}
    53â†’        <div
    54â†’          className="absolute inset-y-0 left-0 bg-gradient-to-r from-transparent via-white/20 to-transparent rounded-full animate-shimmer"
    55â†’          style={{ width: `${progress}%` }}
    56â†’        />
    57â†’      </div>
    58â†’
    59â†’      {/* Step indicators */}
    60â†’      <div className="flex justify-between mt-2">
    61â†’        {Array.from({ length: totalSteps }, (_, i) => i + 1).map((step) => (
    62â†’          <div
    63â†’            key={step}
    64â†’            className={cn(
    65â†’              "w-2 h-2 rounded-full transition-all duration-300",
    66â†’              step < currentStep
    67â†’                ? "bg-emerald-500"
    68â†’                : step === currentStep
    69â†’                ? "bg-teal-500 ring-2 ring-teal-500/30"
    70â†’                : "bg-muted"
    71â†’            )}
    72â†’          />
    73â†’        ))}
    74â†’      </div>
    75â†’    </div>
    76â†’  );
    77â†’});
    78â†’
    79â†’export default ProtocolProgress;
    80â†’

