        <div className="w-7 h-7 rounded-full bg-purple-100" />
        <div className="h-5 bg-purple-100 rounded w-24" />
      </div>
      <div className="space-y-2 mb-4">
        <div className="h-4 bg-purple-100 rounded w-32" />
        <div className="h-4 bg-purple-100 rounded w-28" />
      </div>
