    left: var(--sidebar-collapsed);
  }
  .pc-main {
    margin-left: var(--sidebar-collapsed);
  }
  .pc-commandbar {
    left: var(--sidebar-collapsed);
  }
}

@media (max-width: 1023px) {
  .pc-layout,
  .pc-sidebar,
  .pc-topbar,
  .pc-commandbar {
    display: none;
  }
}

