          <defs>
            <linearGradient id="emotionGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgb(251, 113, 133)" stopOpacity="0.3" />
              <stop offset="100%" stopColor="rgb(236, 72, 153)" stopOpacity="0.3" />
            </linearGradient>
          </defs>

          {/* Area under the line */}
          <polygon
            points={`0,100 ${points} 100,100`}
            fill="url(#emotionGradient)"
          />

          {/* Line */}
          <polyline
            points={points}
            fill="none"
            stroke="rgb(236, 72, 153)"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
          />

