// MePanel Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function MePanel({
  mockDataLevel = "A",
  onEditProfile,
  onLogout,
  className,
}: MePanelProps) {
  const router = useRouter();
  const { data, isLoading, error, refresh } = useMeData({ mockLevel: mockDataLevel });

  // Navigation handlers
  const handleExploreBazi = () => {
    router.push("/chat?skill=bazi&prompt=æ·±å…¥äº†è§£æˆ‘çš„å‘½ç›˜");
  };

  const handleExploreZodiac = () => {
    router.push("/chat?skill=zodiac&prompt=åˆ†ææˆ‘çš„æ˜Ÿç›˜");
  };

  const handleExploreArchetype = () => {
    router.push("/chat?skill=vibe_id&prompt=æ·±å…¥äº†è§£æˆ‘çš„åŸå‹");
  };

  const handleStartChat = () => {
    router.push("/chat");
  };

  // Loading state
  if (isLoading) {
    return <MePanelSkeleton />;
  }

  // Error state
  if (error) {
    return (
      <div className={cn("h-full overflow-y-auto p-5", className)}>
        <div className="text-center py-10">
          <p className="text-sm text-muted-foreground mb-4">
            åŠ è½½å¤±è´¥ï¼Œè¯·é‡è¯•
          </p>
          <button
            onClick={refresh}
            className="px-4 py-2 bg-vellum-100 hover:bg-vellum-200 text-vellum-700 rounded-lg text-sm font-medium transition-colors"
          >
            é‡æ–°åŠ è½½
          </button>
        </div>
      </div>
    );
  }

  // No data (shouldn't happen, but handle gracefully)
  if (!data) {
    return <MePanelSkeleton />;
  }

  return (
    <div className={cn("h-full overflow-y-auto p-5 space-y-5", className)}>
      {/* User Info Section */}
      <section>
        <UserInfoCard
          displayName={data.user.display_name}
          avatarUrl={data.user.avatar_url}
          tagline={data.user.tagline}
          onEditProfile={onEditProfile}
        />
      </section>

      {/* Content based on data level */}
      {data.data_level === "A" ? (
        <LevelAContent
          data={data}
          onExploreBazi={handleExploreBazi}
          onExploreZodiac={handleExploreZodiac}
          onExploreArchetype={handleExploreArchetype}
        />
      ) : data.data_level === "B" ? (
        <LevelBContent
          data={data}
          onExploreBazi={handleExploreBazi}
          onExploreZodiac={handleExploreZodiac}
        />
      ) : (
        <LevelCContent
          onStartChat={handleStartChat}
          onExploreBazi={handleExploreBazi}
          onExploreZodiac={handleExploreZodiac}
        />
      )}

      {/* Settings Section (only for logged-in users) */}
      {data.data_level !== "C" ? (
        <SettingsSection
          onLogout={onLogout}
        />
      ) : null}
    </div>
  );
}

export default MePanel;

