    19â†’ * ç»„åˆæ‰€æœ‰ Dashboard Layer ç»„ä»¶:
    20â†’ * - Layer 0: AmbientHeader
    21â†’ * - Layer 1: LifecoachCard
    22â†’ * - Layer 2: SkillCarousel (My Skills)
    23â†’ * - Layer 3: DiscoverCarousel
    24â†’ */
    25â†’export function DashboardContent({ className }: DashboardContentProps) {
    26â†’  const router = useRouter();
    27â†’  const {
    28â†’    dashboard,
    29â†’    isLoading,
    30â†’    error,
    31â†’    checkIn,
    32â†’    toggleLever,
    33â†’    toggleRock,
    34â†’    subscribeSkill,
    35â†’  } = useDashboard();
    36â†’
    37â†’  // Loading state
    38â†’  if (isLoading) {
    39â†’    return <DashboardSkeleton />;
    40â†’  }
    41â†’
    42â†’  // Error state
    43â†’  if (error) {
    44â†’    return (
    45â†’      <div className="rounded-2xl bg-white/90 p-8 text-center">
    46â†’        <span className="text-3xl mb-2 block">âš ï¸</span>
    47â†’        <p className="text-gray-600 mb-4">åŠ è½½æ•°æ®æ—¶å‡ºé”™</p>
    48â†’        <button
    49â†’          onClick={() => window.location.reload()}
    50â†’          className="px-4 py-2 bg-amber-500 text-white rounded-lg hover:bg-amber-600"
    51â†’        >
    52â†’          é‡è¯•
    53â†’        </button>
    54â†’      </div>
    55â†’    );
    56â†’  }
    57â†’
    58â†’  // No data state
    59â†’  if (!dashboard) {
    60â†’    return <DashboardSkeleton />;
    61â†’  }
    62â†’
    63â†’  const handleSkillCardClick = (skillId: string) => {
    64â†’    router.push(`/chat?skill=${skillId}`);
    65â†’  };
    66â†’
    67â†’  const handleNorthStarClick = () => {
    68â†’    router.push("/chat?skill=lifecoach");
    69â†’  };
    70â†’
    71â†’  return (
    72â†’    <MotionProvider>
    73â†’      <div className={cn("space-y-4", className)}>
    74â†’        {/* Layer 0: Ambient */}
    75â†’        <AmbientHeader
