     9â†’
    10â†’import useSWR from "swr";
    11â†’import { useCallback, useEffect } from "react";
    12â†’import { apiClient } from "@/lib/api";
    13â†’import type {
    14â†’  DashboardDTO,
    15â†’  DashboardResponse,
    16â†’  CheckinResponse,
    17â†’  LeverToggleResponse,
    18â†’  SkillSubscribeResponse,
    19â†’  SkillCardData,
    20â†’} from "@/types/dashboard";
    21â†’
    22â†’const DASHBOARD_KEY = "/dashboard";
    23â†’const CACHE_KEY = "vibelife-dashboard-cache";
    24â†’
    25â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
    26â†’// Local Storage Cache for Offline Support
    27â†’// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
    28â†’
    29â†’function getCachedData(): DashboardDTO | undefined {
    30â†’  if (typeof window === "undefined") return undefined;
    31â†’  try {
    32â†’    const cached = localStorage.getItem(CACHE_KEY);
    33â†’    if (cached) {
    34â†’      const parsed = JSON.parse(cached);
    35â†’      // Check if cache is from today (for daily data freshness)
    36â†’      const cachedDate = new Date(parsed.generatedAt).toDateString();
    37â†’      const today = new Date().toDateString();
    38â†’      if (cachedDate === today) {
    39â†’        return parsed;
    40â†’      }
    41â†’    }
    42â†’  } catch {
    43â†’    // Ignore parse errors
    44â†’  }
    45â†’  return undefined;
    46â†’}
    47â†’
    48â†’function setCachedData(data: DashboardDTO): void {
    49â†’  if (typeof window === "undefined") return;
    50â†’  try {
    51â†’    localStorage.setItem(CACHE_KEY, JSON.stringify(data));
    52â†’  } catch {
    53â†’    // Ignore storage errors (quota exceeded, etc.)
    54â†’  }
    55â†’}
    56â†’
    57â†’// Fetcher function
    58â†’async function fetcher(url: string): Promise<DashboardDTO> {
    59â†’  const { data } = await apiClient.get<DashboardResponse>(url);
    60â†’  return data.data;
    61â†’}
    62â†’
    63â†’export function useDashboard() {
    64â†’  const { data, error, isLoading, mutate } = useSWR<DashboardDTO>(
    65â†’    DASHBOARD_KEY,
    66â†’    fetcher,
    67â†’    {
    68â†’      revalidateOnFocus: false,
    69â†’      revalidateOnReconnect: true,
    70â†’      dedupingInterval: 60000, // 1 minute dedup
    71â†’      errorRetryCount: 3,
    72â†’      fallbackData: getCachedData(), // Offline support
    73â†’      onSuccess: (data) => {
    74â†’        // Cache successful responses for offline use
    75â†’        setCachedData(data);
    76â†’      },
    77â†’    }
    78â†’  );
    79â†’
    80â†’  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
    81â†’  // Actions with optimistic updates
    82â†’  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
    83â†’
    84â†’  /**
    85â†’   * ç­¾åˆ°
    86â†’   */
    87â†’  const checkIn = useCallback(async () => {
    88â†’    if (!data) return;
    89â†’
    90â†’    const currentStreak = data.status.streak;
    91â†’
    92â†’    // 1. Optimistic update
    93â†’    await mutate(
    94â†’      {
    95â†’        ...data,
    96â†’        status: {
    97â†’          ...data.status,
    98â†’          checkedIn: true,
    99â†’          streak: currentStreak + 1,
   100â†’        },
   101â†’      },
   102â†’      false
   103â†’    );
   104â†’
   105â†’    try {
   106â†’      // 2. API call
   107â†’      const { data: result } = await apiClient.post<CheckinResponse>(
   108â†’        "/dashboard/checkin"
   109â†’      );
   110â†’
   111â†’      // 3. Update with server data
   112â†’      await mutate(
   113â†’        {
   114â†’          ...data,
   115â†’          status: {
   116â†’            ...data.status,
   117â†’            checkedIn: true,
   118â†’            streak: result.data.streak,
   119â†’          },
   120â†’        },
   121â†’        false
   122â†’      );
   123â†’
   124â†’      return result.data;
   125â†’    } catch (err) {
   126â†’      // 4. Rollback on error
   127â†’      await mutate(data, false);
   128â†’      throw err;
   129â†’    }
   130â†’  }, [data, mutate]);
   131â†’
   132â†’  /**
   133â†’   * åˆ‡æ¢æ æ†å®ŒæˆçŠ¶æ€
   134â†’   */
   135â†’  const toggleLever = useCallback(
   136â†’    async (leverId: string) => {
   137â†’      if (!data) return;
   138â†’
   139â†’      const lever = data.lifecoach.todayLevers.find((l) => l.id === leverId);
   140â†’      if (!lever) return;
   141â†’
   142â†’      const newCompleted = !lever.completed;
   143â†’
   144â†’      // 1. Optimistic update
   145â†’      const optimisticData = {
   146â†’        ...data,
   147â†’        lifecoach: {
   148â†’          ...data.lifecoach,
   149â†’          todayLevers: data.lifecoach.todayLevers.map((l) =>
   150â†’            l.id === leverId ? { ...l, completed: newCompleted } : l
   151â†’          ),
   152â†’        },
   153â†’      };
   154â†’      await mutate(optimisticData, false);
   155â†’
   156â†’      try {
   157â†’        // 2. API call
   158â†’        await apiClient.patch<LeverToggleResponse>(
   159â†’          `/dashboard/lever/${leverId}`,
   160â†’          { completed: newCompleted }
   161â†’        );
   162â†’
   163â†’        // Keep optimistic data (server will confirm)
   164â†’      } catch (err) {
   165â†’        // 3. Rollback on error
   166â†’        await mutate(data, false);
   167â†’        throw err;
   168â†’      }
   169â†’    },
   170â†’    [data, mutate]
   171â†’  );
   172â†’
   173â†’  /**
   174â†’   * åˆ‡æ¢å¤§çŸ³å¤´å®ŒæˆçŠ¶æ€
   175â†’   */
   176â†’  const toggleRock = useCallback(
   177â†’    async (rockId: string) => {
   178â†’      if (!data) return;
   179â†’
   180â†’      const rock = data.lifecoach.weekRocks.find((r) => r.id === rockId);
   181â†’      if (!rock) return;
   182â†’
   183â†’      const newCompleted = !rock.completed;
   184â†’
   185â†’      // 1. Optimistic update
   186â†’      const optimisticData = {
   187â†’        ...data,
   188â†’        lifecoach: {
   189â†’          ...data.lifecoach,
   190â†’          weekRocks: data.lifecoach.weekRocks.map((r) =>
   191â†’            r.id === rockId ? { ...r, completed: newCompleted } : r
   192â†’          ),
   193â†’        },
   194â†’      };
   195â†’      await mutate(optimisticData, false);
   196â†’
   197â†’      try {
   198â†’        // 2. API call
