   203â†’        // 3. Rollback on error
   204â†’        await mutate(data, false);
   205â†’        throw err;
   206â†’      }
   207â†’    },
   208â†’    [data, mutate]
   209â†’  );
   210â†’
   211â†’  /**
   212â†’   * è®¢é˜… Skill
   213â†’   */
   214â†’  const subscribeSkill = useCallback(
   215â†’    async (skillId: string) => {
   216â†’      if (!data) return;
   217â†’
   218â†’      // Find the skill in discover
   219â†’      const targetSkill = data.discover.find((s) => s.skillId === skillId);
   220â†’      if (!targetSkill) return;
   221â†’
   222â†’      // 1. Optimistic update: move from discover to mySkills (with placeholder)
   223â†’      const placeholderCard: SkillCardData = {
   224â†’        skillId,
   225â†’        cardId: "loading",
   226â†’        title: targetSkill.title,
   227â†’        icon: targetSkill.icon,
   228â†’        content: {
   229â†’          headline: "åŠ è½½ä¸­...",
   230â†’          insights: [],
   231â†’        },
   232â†’        computedAt: new Date().toISOString(),
   233â†’        actionLabel: "åŠ è½½ä¸­...",
   234â†’        actionRoute: "",
   235â†’      };
   236â†’
   237â†’      const optimisticData = {
   238â†’        ...data,
   239â†’        mySkills: [...data.mySkills, placeholderCard],
   240â†’        discover: data.discover.filter((s) => s.skillId !== skillId),
   241â†’      };
   242â†’      await mutate(optimisticData, false);
   243â†’
   244â†’      try {
   245â†’        // 2. API call
   246â†’        await apiClient.post(`/skills/${skillId}/subscribe`);
   247â†’
   248â†’        // 3. Update with placeholder card (backend doesn't return card data yet)
   249â†’        // Create a proper placeholder card from the discover item
   250â†’        const subscribedCard: SkillCardData = {
   251â†’          skillId,
   252â†’          cardId: `${skillId}-subscribed`,
   253â†’          title: targetSkill.title,
   254â†’          icon: targetSkill.icon,
   255â†’          content: {
   256â†’            headline: "å·²è®¢é˜…",
   257â†’            insights: [targetSkill.description],
   258â†’          },
   259â†’          computedAt: new Date().toISOString(),
   260â†’          actionLabel: "å¼€å§‹ä½¿ç”¨",
   261â†’          actionRoute: `/chat?skill=${skillId}`,
   262â†’        };
   263â†’
   264â†’        await mutate(
   265â†’          {
   266â†’            ...optimisticData,
   267â†’            mySkills: optimisticData.mySkills.map((s) =>
   268â†’              s.skillId === skillId ? subscribedCard : s
   269â†’            ),
   270â†’          },
   271â†’          false
   272â†’        );
   273â†’
   274â†’        return { subscription: { skillId, subscribedAt: new Date().toISOString() }, card: subscribedCard };
   275â†’      } catch (err) {
   276â†’        // 4. Rollback on error
   277â†’        await mutate(data, false);
   278â†’        throw err;
   279â†’      }
   280â†’    },
   281â†’    [data, mutate]
   282â†’  );
   283â†’
   284â†’  /**
   285â†’   * åˆ·æ–°æ•°æ®
   286â†’   */
   287â†’  const refresh = useCallback(() => {
   288â†’    return mutate();
   289â†’  }, [mutate]);
   290â†’
   291â†’  return {
   292â†’    // Data
   293â†’    dashboard: data,
   294â†’    isLoading,
   295â†’    error,
   296â†’
   297â†’    // Computed
   298â†’    isCheckedIn: data?.status.checkedIn ?? false,
   299â†’    streak: data?.status.streak ?? 0,
   300â†’
   301â†’    // Actions
   302â†’    checkIn,
   303â†’    toggleLever,
   304â†’    toggleRock,
   305â†’    subscribeSkill,
   306â†’    refresh,
   307â†’  };
   308â†’}
   309â†’
   310â†’export default useDashboard;
