    19â†’
    20â†’/**
    21â†’ * SkillCard - å·²è®¢é˜… Skill çš„åŠ¨æ€å†…å®¹å¡ç‰‡
    22â†’ *
    23â†’ * æ”¯æŒä¸¤ç§å†…å®¹ç±»å‹:
    24â†’ * 1. é€šç”¨å‹: headline + insights
    25â†’ * 2. è¯„åˆ†å‹: rating + suggestions (å…«å­—/æ˜Ÿåº§)
    26â†’ */
    27â†’export const SkillCard = memo(function SkillCard({
    28â†’  skillId,
    29â†’  icon,
    30â†’  title,
    31â†’  content,
    32â†’  actionLabel,
    33â†’  actionRoute,
    34â†’  onClick,
    35â†’  className,
    36â†’}: SkillCardProps) {
    37â†’  const router = useRouter();
    38â†’
    39â†’  const handleClick = () => {
    40â†’    if (onClick) {
    41â†’      onClick();
    42â†’    } else {
    43â†’      router.push(actionRoute);
    44â†’    }
    45â†’  };
    46â†’
    47â†’  const isRatingType = !!content.rating;
    48â†’
    49â†’  return (
    50â†’    <MotionDiv
    51â†’      whileHover={{ y: -2, boxShadow: "var(--shadow-card-hover)" }}
    52â†’      className={cn(
