"use client";

import { useState, KeyboardEvent, useRef, useEffect } from "react";
import { cn } from "@/lib/utils";
import { Send, Mic, Paperclip } from "lucide-react";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ChatInput - LUMINOUS PAPER Design System
// Omnibar ç»ç’ƒèƒ¶å›Šæ ·å¼ï¼šfrosted glass è¾“å…¥æ¡†
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const MAX_MESSAGE_LENGTH = 500;

export interface ChatInputProps {
  onSend: (message: string) => void;
  onFileSelect?: (file: File) => void;
  disabled?: boolean;
  placeholder?: string;
  showMediaButtons?: boolean;
  className?: string;
}

export function ChatInput({
  onSend,
  onFileSelect,
  disabled,
  placeholder = "åˆ†äº«ä½ çš„ç”Ÿè¾°æˆ–æå‡ºé—®é¢˜...",
  showMediaButtons = true,
  className,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Auto-resize textarea
  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${Math.min(textarea.scrollHeight, 120)}px`;
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() && !disabled) {
      onSend(message.trim());
      setMessage("");
      // Reset height
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const value = e.target.value;
    if (value.length <= MAX_MESSAGE_LENGTH) {
      setMessage(value);
    }
  };

  const canSend = message.trim().length > 0 && !disabled;
  const charCount = message.length;
  const isNearLimit = charCount > MAX_MESSAGE_LENGTH * 0.8;

  const handleFileClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file && onFileSelect) {
      onFileSelect(file);
    }
    // Reset input
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  return (
    <div className={cn("p-4 md:px-6 pb-6", className)}>
      {/* Screen reader only label for the chat input */}
      <label htmlFor="chat-input" className="sr-only">
        èŠå¤©è¾“å…¥æ¡† - åˆ†äº«ä½ çš„ç”Ÿè¾°æˆ–æå‡ºé—®é¢˜
      </label>

      {/* Hidden file input with label */}
      <label htmlFor="file-upload" className="sr-only">
        ä¸Šä¼ å›¾ç‰‡æˆ–æ–‡æ¡£
      </label>
      <input
        ref={fileInputRef}
        id="file-upload"
        type="file"
        accept="image/*,.pdf,.txt,.docx"
        onChange={handleFileChange}
        className="hidden"
        disabled={disabled}
      />

      {/* Gradient fade above input */}
      <div className="h-8 bg-gradient-to-t from-background to-transparent -mt-8 mb-4 pointer-events-none" />

      {/* æ‰€æœ‰è®¾å¤‡åº•éƒ¨å±…ä¸­ï¼Œæœ€å¤§å®½åº¦é™åˆ¶ */}
      <div className="max-w-3xl mx-auto">
        <div className="omnibar shadow-lg shadow-black/5">
          {/* Media buttons (optional) */}
          {showMediaButtons && (
            <>
              <button
                type="button"
                onClick={handleFileClick}
                className="omnibar-btn"
                aria-label="ä¸Šä¼ æ–‡ä»¶"
                aria-describedby="upload-hint"
                title="ä¸Šä¼ å›¾ç‰‡æˆ–æ–‡æ¡£"
                disabled={disabled}
              >
                <Paperclip className="w-5 h-5" />
              </button>
              <button
                type="button"
                className="omnibar-btn"
                aria-label="è¯­éŸ³è¾“å…¥"
                title="è¯­éŸ³è¾“å…¥ï¼ˆå³å°†æ¨å‡ºï¼‰"
                disabled={disabled}
              >
                <Mic className="w-5 h-5" />
              </button>
            </>
          )}

          {/* Input */}
          <textarea
            ref={textareaRef}
            id="chat-input"
            value={message}
            onChange={handleChange}
            onKeyDown={handleKeyDown}
            placeholder={placeholder}
            disabled={disabled}
            rows={1}
            className="omnibar-input min-h-[24px] max-h-[120px]"
            aria-describedby="input-hint"
            aria-multiline="true"
            maxLength={MAX_MESSAGE_LENGTH}
          />

          {/* Send button with animation */}
          <button
            type="button"
            onClick={handleSend}
            disabled={!canSend}
            className={cn(
              "omnibar-send transition-all duration-300",
              canSend
                ? "scale-100 opacity-100 bg-skill-primary hover:bg-skill-primary/90"
                : "scale-90 opacity-30 cursor-not-allowed bg-muted"
            )}
            aria-label="å‘é€æ¶ˆæ¯"
            title={canSend ? "å‘é€æ¶ˆæ¯ (Enter)" : "è¯·è¾“å…¥å†…å®¹åå‘é€"}
          >
            <Send className="w-5 h-5" />
          </button>
        </div>

        {/* Footer: Keyboard hints + Character count */}
        <div className="flex items-center justify-between mt-2 px-1">
          <p id="input-hint" className="text-xs text-muted-foreground/60">
            <kbd className="px-1.5 py-0.5 bg-muted/50 rounded text-[10px] font-mono">Enter</kbd>
            <span className="mx-1">å‘é€</span>
            <kbd className="px-1.5 py-0.5 bg-muted/50 rounded text-[10px] font-mono">Shift+Enter</kbd>
            <span className="mx-1">æ¢è¡Œ</span>
          </p>
          <p className={cn(
            "text-xs transition-colors",
            isNearLimit ? "text-accent-500" : "text-muted-foreground/40"
          )}>
            {charCount} / {MAX_MESSAGE_LENGTH}
          </p>
        </div>
      </div>
    </div>
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// QuickReplies - Suggested quick reply buttons
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface QuickRepliesProps {
  replies: string[];
  onSelect: (reply: string) => void;
  className?: string;
}

export function QuickReplies({ replies, onSelect, className }: QuickRepliesProps) {
  if (replies.length === 0) return null;

  return (
    <div className={cn("flex flex-wrap gap-2 px-4 pb-2", className)}>
      {replies.map((reply, index) => (
        <button
          key={index}
          onClick={() => onSelect(reply)}
          className={cn(
            "px-3 py-1.5 text-sm rounded-full",
            "bg-muted/50 text-muted-foreground",
            "border border-border/50",
            "hover:bg-muted hover:text-foreground",
            "transition-colors duration-200"
          )}
        >
          {reply}
        </button>
      ))}
    </div>
  );
}

