// FloatingOmnibar - Floating version for landing pages
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface FloatingOmnibarProps {
  onSend: (message: string) => void;
  placeholder?: string;
  className?: string;
}

export function FloatingOmnibar({
  onSend,
  placeholder = "è¾“å…¥ä½ çš„å‡ºç”Ÿæ—¥æœŸå¼€å§‹...",
  className,
}: FloatingOmnibarProps) {
  const [value, setValue] = useState("");

  const handleSubmit = () => {
    if (value.trim()) {
      onSend(value.trim());
      setValue("");
    }
  };

  return (
    <div className={cn("bg-bg-card border border-subtle shadow-soft p-6 w-full md:max-w-lg mx-auto", className)}>
      <div className="omnibar">
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSubmit()}
          placeholder={placeholder}
          className="omnibar-input"
        />
        <button
          onClick={handleSubmit}
          disabled={!value.trim()}
          className={cn(
            "omnibar-send",
            !value.trim() && "opacity-50 cursor-not-allowed"
          )}
        >
          <Send className="w-5 h-5" />
        </button>
      </div>
    </div>
  );
}

