  .pc-nav-item:hover {
    background: var(--bg-secondary);
    color: var(--text-primary);
  }

  .pc-nav-item.active {
    background: var(--bg-tertiary);
    color: var(--text-primary);
    font-weight: 500;
  }

  .pc-topbar {
    position: fixed;
    top: 0;
    left: var(--sidebar-width);
    right: 0;
    height: var(--topbar-height);
    background: rgba(250, 250, 250, 0.8);
    backdrop-filter: blur(12px);
    border-bottom: 1px solid var(--border-subtle);
    z-index: var(--z-sticky);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 20px;
    transition: left var(--duration-slow) var(--ease-out-expo);
  }

  .pc-main {
    margin-left: var(--sidebar-width);
    margin-top: var(--topbar-height);
    margin-bottom: var(--commandbar-height);
    min-height: calc(100vh - var(--topbar-height) - var(--commandbar-height));
    display: flex;
    transition: margin-left var(--duration-slow) var(--ease-out-expo);
  }

  .pc-commandbar {
    position: fixed;
    bottom: 0;
    left: var(--sidebar-width);
    right: 0;
    height: var(--commandbar-height);
    background: var(--bg-primary);
    border-top: 1px solid var(--border-subtle);
    z-index: var(--z-sticky);
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 20px;
    transition: left var(--duration-slow) var(--ease-out-expo);
  }
}

/* å“åº”å¼ */
@media (max-width: 1279px) {
  .pc-sidebar {
    width: var(--sidebar-collapsed);
