import { NextResponse } from "next/server";
import type { NextRequest } from "next/server";

/**
 * Middleware - å­åŸŸåè·¯ç”± + Skill æ£€æµ‹
 *
 * è·¯ç”±è§„åˆ™:
 * - vibelife.app (æ— å­åŸŸå) -> å“ç‰Œé¦–é¡µ (www è·¯ç”±ç»„)
 * - bazi.vibelife.app -> /bazi/* è·¯ç”±ç»„
 * - zodiac.vibelife.app -> /zodiac/* è·¯ç”±ç»„
 *
 * URL é‡å†™ç¤ºä¾‹:
 * - bazi.vibelife.app/ -> /bazi
 * - bazi.vibelife.app/chat -> /bazi/chat
 * - zodiac.vibelife.app/chat -> /zodiac/chat
 *
 * æœ¬åœ°å¼€å‘:
 * - localhost:3000 -> é»˜è®¤æ˜¾ç¤ºå“ç‰Œé¦–é¡µ
 * - localhost:3000/bazi -> bazi è·¯ç”±
 * - localhost:3000/zodiac -> zodiac è·¯ç”±
 */

// æ”¯æŒçš„ skill ç±»å‹
const VALID_SKILLS = ["bazi", "zodiac", "mbti", "attach", "career", "lifecoach"] as const;
type ValidSkill = (typeof VALID_SKILLS)[number];

// å½“å‰å·²å¯ç”¨çš„ skills (æœ‰å¯¹åº”è·¯ç”±ç»„)
const ENABLED_SKILLS: ValidSkill[] = ["bazi", "zodiac", "lifecoach"];

// é»˜è®¤ skill
const DEFAULT_SKILL: ValidSkill = "bazi";

// å…±äº«è·¯ç”±ï¼ˆä¸éœ€è¦ skill å‰ç¼€ï¼‰
const SHARED_ROUTES = ["/auth", "/payment", "/checkout", "/interview"];

// æ£€æŸ¥æ˜¯å¦æ˜¯å…±äº«è·¯ç”±
function isSharedRoute(pathname: string): boolean {
  return SHARED_ROUTES.some((route) => pathname.startsWith(route));
}

// æ£€æŸ¥è·¯å¾„æ˜¯å¦å·²ç»åŒ…å« skill å‰ç¼€
function hasSkillPrefix(pathname: string): boolean {
  const firstSegment = pathname.split("/")[1];
  return VALID_SKILLS.includes(firstSegment as ValidSkill);
}

export default function middleware(request: NextRequest) {
  const url = request.nextUrl.clone();
  const hostname = request.headers.get("host") || "";
  const pathname = url.pathname;

  // æå–å­åŸŸå
  let detectedSkill: ValidSkill | null = null;
  const parts = hostname.split(".");

  // æ£€æµ‹å­åŸŸå
  // æ ¼å¼: {skill}.vibelife.app æˆ– {skill}.localhost:xxxx
  if (parts.length >= 2) {
    const subdomain = parts[0].toLowerCase();
    if (ENABLED_SKILLS.includes(subdomain as ValidSkill)) {
      detectedSkill = subdomain as ValidSkill;
    }
  }

  // æ£€æŸ¥ URL å‚æ•° ?skill=xxx
  const urlSkillParam = url.searchParams.get("skill");
  const urlSkill = urlSkillParam && VALID_SKILLS.includes(urlSkillParam as ValidSkill)
    ? (urlSkillParam as ValidSkill)
    : null;

  // ç¡®å®šæœ€ç»ˆä½¿ç”¨çš„ skill
  // ä¼˜å…ˆçº§: URL å‚æ•° > å­åŸŸå > è·¯å¾„å‰ç¼€ > é»˜è®¤
  let skill: ValidSkill;
  let shouldRewrite = false;

  if (urlSkill) {
    // URL å‚æ•°ä¼˜å…ˆçº§æœ€é«˜
    skill = urlSkill;
  } else if (detectedSkill) {
    // æœ‰å­åŸŸåï¼Œä½¿ç”¨å­åŸŸåå¯¹åº”çš„ skill
    skill = detectedSkill;

    // å¦‚æœè·¯å¾„ä¸æ˜¯å…±äº«è·¯ç”±ä¸”æ²¡æœ‰ skill å‰ç¼€ï¼Œéœ€è¦é‡å†™
    if (!isSharedRoute(pathname) && !hasSkillPrefix(pathname)) {
      shouldRewrite = true;
    }
  } else {
    // æ— å­åŸŸå
    // æ£€æŸ¥è·¯å¾„æ˜¯å¦å·²æœ‰ skill å‰ç¼€
    const firstSegment = pathname.split("/")[1];
    if (ENABLED_SKILLS.includes(firstSegment as ValidSkill)) {
      skill = firstSegment as ValidSkill;
    } else {
      skill = DEFAULT_SKILL;
    }
  }

  // è®¾ç½® skill åˆ°è¯·æ±‚å¤´
  const requestHeaders = new Headers(request.headers);
  requestHeaders.set("x-vibelife-skill", skill);

  // å¤„ç† URL é‡å†™ï¼ˆä»…å½“æœ‰å­åŸŸåæ—¶ï¼‰
  if (shouldRewrite && detectedSkill) {
    // é‡å†™è·¯å¾„ï¼šæ·»åŠ  skill å‰ç¼€
    // / -> /bazi
    // /chat -> /bazi/chat
    const newPathname = pathname === "/" ? `/${detectedSkill}` : `/${detectedSkill}${pathname}`;
    url.pathname = newPathname;

    const response = NextResponse.rewrite(url, {
      request: { headers: requestHeaders },
    });

    // è®¾ç½® cookie
    response.cookies.set("vibelife-skill", skill, {
      path: "/",
      sameSite: "lax",
      secure: process.env.NODE_ENV === "production",
      maxAge: 60 * 60 * 24 * 365,
    });

    return response;
  }

  // æ™®é€šè¯·æ±‚ï¼ˆæ— é‡å†™ï¼‰
  const response = NextResponse.next({
    request: { headers: requestHeaders },
  });

  // è®¾ç½® cookie
  response.cookies.set("vibelife-skill", skill, {
    path: "/",
    sameSite: "lax",
    secure: process.env.NODE_ENV === "production",
    maxAge: 60 * 60 * 24 * 365,
  });

  return response;
}

export const config = {
  matcher: [
    /*
     * Match all request paths except:
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public folder files
     * - api routes
     */
    "/((?!_next/static|_next/image|favicon.ico|.*\\..*|api).*)",
  ],
};

