      const response = await fetch('/api/v1/relationship/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          user_id: userId,
          user_profile: userProfile,
          partner_info: partnerData,
          relationship_type: relationshipType,
