"use client";

import { useState, useRef, useEffect } from "react";
import { ChatMessage } from "./ChatMessage";
import { ChatInput } from "./ChatInput";
import { InsightCard, InsightType } from "@/components/insight/InsightCard";
import { VibeGlyph, BreathAura, type SkillType } from "@/components/core";
import { getTokens, sendGuestMessage, sendMessage, ChatResponse } from "@/lib/api";

interface Message {
  id: string;
  role: "user" | "assistant";
  content: string;
  timestamp?: string;
  insight?: {
    id: string;
    insight_type: InsightType;
    title: string;
    content: string;
  };
}

export type VoiceMode = "warm" | "sarcastic";

export interface ChatContainerProps {
  skillId: string;
  skill?: SkillType;
  conversationId?: string;
  voiceMode?: VoiceMode;
  onConversationStart?: (id: string) => void;
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ChatEmptyState - LUMINOUS PAPER Design System
// ç©ºçŠ¶æ€ï¼šå±…ä¸­ VibeGlyph + æŠ€èƒ½æ„ŸçŸ¥æ–‡æ¡ˆ + å‘¼å¸å…‰æ™•
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const SKILL_EMPTY_STATE: Record<SkillType, { title: string; subtitle: string }> = {
  bazi: {
    title: "æ¢ç´¢ä½ çš„å‘½ç†",
    subtitle: "åˆ†äº«ä½ çš„ç”Ÿè¾°ï¼Œå¼€å¯å…«å­—è§£è¯»ä¹‹æ—…",
  },
  zodiac: {
    title: "å€¾å¬æ˜Ÿè¾°çš„å£°éŸ³",
    subtitle: "è®©æ˜Ÿç›˜ä¸ºä½ æ­ç¤ºå®‡å®™çš„å¯†è¯­",
  },
  mbti: {
    title: "å‘ç°çœŸå®çš„è‡ªå·±",
    subtitle: "é€šè¿‡å¯¹è¯ï¼Œæ¢ç´¢ä½ çš„äººæ ¼ç‰¹è´¨",
  },
  attach: {
    title: "ç†è§£ä½ çš„ä¾æ‹æ¨¡å¼",
    subtitle: "æ¢ç´¢äº²å¯†å…³ç³»ä¸­çš„è‡ªæˆ‘",
  },
  career: {
    title: "è§„åˆ’ä½ çš„èŒä¸šè“å›¾",
    subtitle: "è®©æˆ‘ä»¬ä¸€èµ·æ¢ç´¢ä½ çš„èŒä¸šå‘å±•æ–¹å‘",
  },
};

// Quick prompts for each skill
const SKILL_QUICK_PROMPTS: Record<SkillType, string[]> = {
  bazi: [
    "æˆ‘æ˜¯1990å¹´5æœˆ15æ—¥æ—©ä¸Š8ç‚¹å‡ºç”Ÿçš„",
    "å¸®æˆ‘åˆ†æä»Šå¹´çš„è¿åŠ¿",
    "æˆ‘çš„äº‹ä¸šè¿å¦‚ä½•ï¼Ÿ",
    "ä»€ä¹ˆæ—¶å€™é€‚åˆåšé‡å¤§å†³å®šï¼Ÿ",
  ],
  zodiac: [
    "æˆ‘æ˜¯åŒå­åº§ï¼Œå¸®æˆ‘çœ‹çœ‹æœ¬å‘¨è¿åŠ¿",
    "æˆ‘çš„å¤ªé˜³æ˜Ÿåº§å’Œä¸Šå‡æ˜Ÿåº§æœ‰ä»€ä¹ˆåŒºåˆ«ï¼Ÿ",
    "åŒå­åº§å’Œä»€ä¹ˆæ˜Ÿåº§æœ€é…ï¼Ÿ",
    "æ°´é€†å¯¹æˆ‘æœ‰ä»€ä¹ˆå½±å“ï¼Ÿ",
  ],
  mbti: [
    "å¸®æˆ‘æµ‹æµ‹æˆ‘çš„MBTIç±»å‹",
    "INFPé€‚åˆä»€ä¹ˆèŒä¸šï¼Ÿ",
    "æˆ‘æ˜¯INTJï¼Œå¦‚ä½•æ”¹å–„äººé™…å…³ç³»ï¼Ÿ",
    "Eäººå’ŒIäººæœ‰ä»€ä¹ˆæœ¬è´¨åŒºåˆ«ï¼Ÿ",
  ],
  attach: [
    "å¸®æˆ‘åˆ†ææˆ‘çš„ä¾æ‹ç±»å‹",
    "å›é¿å‹ä¾æ‹å¦‚ä½•å»ºç«‹äº²å¯†å…³ç³»ï¼Ÿ",
    "ä¸ºä»€ä¹ˆæˆ‘æ€»æ˜¯å®³æ€•è¢«æŠ›å¼ƒï¼Ÿ",
    "å¦‚ä½•æ²»æ„ˆç«¥å¹´çš„æƒ…æ„Ÿåˆ›ä¼¤ï¼Ÿ",
  ],
  career: [
    "æˆ‘è¯¥å¦‚ä½•è§„åˆ’èŒä¸šå‘å±•ï¼Ÿ",
    "æˆ‘é€‚åˆåˆ›ä¸šè¿˜æ˜¯æ‰“å·¥ï¼Ÿ",
    "å¦‚ä½•åœ¨é¢è¯•ä¸­å±•ç°è‡ªå·±ï¼Ÿ",
    "è½¬è¡Œéœ€è¦è€ƒè™‘ä»€ä¹ˆï¼Ÿ",
  ],
