function ChatEmptyState({ skill, onQuickPrompt }: { skill: SkillType; onQuickPrompt?: (prompt: string) => void }) {
  const config = SKILL_EMPTY_STATE[skill] || SKILL_EMPTY_STATE.bazi;
  const quickPrompts = SKILL_QUICK_PROMPTS[skill] || SKILL_QUICK_PROMPTS.bazi;

  return (
    <div className="flex flex-col items-center justify-center max-w-xl mx-auto px-4 py-8">
      {/* Animated glyph with enhanced aura */}
      <div className="relative flex items-center justify-center mb-6">
        {/* Outer pulsing ring */}
        <div className="absolute w-20 h-20 rounded-full border border-skill-primary/20 animate-pulse-slow" />
        <div className="absolute w-28 h-28 rounded-full border border-skill-primary/10 animate-pulse-slower" />

        {/* Breath aura */}
        <BreathAura
          skill={skill}
          size="md"
          position="center"
          intensity="medium"
          className="opacity-40"
        />

        {/* Central glyph */}
        <VibeGlyph
          size="sm"
          skill={skill}
          showAura={true}
          animate={true}
          className="relative z-10"
        />
      </div>

      {/* Title */}
      <h3 className="text-lg md:text-xl font-serif font-semibold text-foreground mb-1 text-center">
        {config.title}
      </h3>

      {/* Subtitle */}
      <p className="text-sm text-muted-foreground max-w-sm text-center mb-6">
        {config.subtitle}
      </p>

      {/* Quick prompt suggestions */}
      <div className="w-full space-y-2">
        <p className="text-xs text-muted-foreground/60 text-center mb-2">
          è¯•è¯•è¿™æ ·é—®
        </p>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
