"use client";

import Link from "next/link";
import { ArrowLeft, FileText, MessageSquare, LayoutDashboard, Settings } from "lucide-react";
import { LuminousPaper, VibeGlyph, BreathAura, type SkillType } from "@/components/core";
import { IconButton } from "@/components/ui/Button";
import { InsightPanel, MobileInsightBar, type InsightPanelProps } from "@/components/insight/InsightPanel";
import { cn } from "@/lib/utils";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// ChatLayout - LUMINOUS PAPER Design System
// ç»Ÿä¸€èŠå¤©é¡µé¢å¸ƒå±€ï¼š
// - PCç«¯ï¼šåŒæ å¸ƒå±€ï¼ˆå·¦ä¾§ Insight Panel + å³ä¾§ Chat Streamï¼‰
// - ç§»åŠ¨ç«¯ï¼šå•æ å¸ƒå±€ï¼ˆå¯æŠ˜å é¡¶éƒ¨ Insight Bar + Chat Streamï¼‰
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

type VoiceMode = "warm" | "sarcastic";

interface ChatLayoutProps {
  skill: SkillType;
  title: string;
  backHref: string;
  onViewProfile?: () => void;
  children: React.ReactNode;
  // Insight panel props
  profile?: InsightPanelProps["profile"];
  insights?: InsightPanelProps["insights"];
  onInsightClick?: InsightPanelProps["onInsightClick"];
  // Tab navigation (optional)
  activeTab?: "chat" | "chart" | "settings";
  onTabChange?: (tab: "chat" | "chart" | "settings") => void;
  // Voice mode
  voiceMode?: VoiceMode;
  onVoiceModeToggle?: () => void;
}

const SKILL_CONFIG: Record<SkillType, { profileLabel: string; chartLabel: string }> = {
  bazi: { profileLabel: "æŸ¥çœ‹å‘½ç›˜", chartLabel: "å‘½ç›˜" },
  zodiac: { profileLabel: "æŸ¥çœ‹æ˜Ÿç›˜", chartLabel: "æ˜Ÿç›˜" },
  mbti: { profileLabel: "æŸ¥çœ‹æ¡£æ¡ˆ", chartLabel: "æ¡£æ¡ˆ" },
  attach: { profileLabel: "æŸ¥çœ‹æ¡£æ¡ˆ", chartLabel: "æ¡£æ¡ˆ" },
  career: { profileLabel: "æŸ¥çœ‹æ¡£æ¡ˆ", chartLabel: "æ¡£æ¡ˆ" },
};

export function ChatLayout({
  skill,
  title,
  backHref,
  onViewProfile,
  children,
  profile,
  insights,
  onInsightClick,
  activeTab = "chat",
  onTabChange,
  voiceMode = "warm",
  onVoiceModeToggle,
}: ChatLayoutProps) {
  const config = SKILL_CONFIG[skill];

  return (
    <LuminousPaper skill={skill} variant="default" className="h-screen flex flex-col">
      {/* Background aura */}
      <BreathAura skill={skill} size="xl" position="top" intensity="low" className="opacity-40" />

      {/* Glass Header */}
      <header className="nav">
        <div className="nav-container">
          {/* Left: Back + Logo + Title */}
          <div className="flex items-center gap-3">
            <Link href={backHref} className="nav-link flex items-center gap-1">
              <ArrowLeft className="w-4 h-4" />
              <span className="hidden sm:inline">è¿”å›</span>
            </Link>
            <div className="flex items-center gap-2">
              <VibeGlyph size="sm" skill={skill} showAura={false} animate={false} />
              <span className="font-serif font-semibold text-foreground">{title}</span>
            </div>
          </div>

          {/* Center: Tab Navigation (PC only) */}
          {onTabChange && (
            <div className="hidden lg:flex items-center gap-1 bg-muted/30 rounded-full p-1">
              <button
                onClick={() => onTabChange("chat")}
                className={cn(
                  "px-4 py-1.5 text-sm rounded-full transition-all duration-200",
                  activeTab === "chat"
                    ? "bg-card text-foreground shadow-sm"
                    : "text-muted-foreground hover:text-foreground"
                )}
              >
                <MessageSquare className="w-4 h-4 inline mr-1.5" />
                å¯¹è¯
              </button>
              <button
                onClick={() => onTabChange("chart")}
                className={cn(
                  "px-4 py-1.5 text-sm rounded-full transition-all duration-200",
                  activeTab === "chart"
                    ? "bg-card text-foreground shadow-sm"
                    : "text-muted-foreground hover:text-foreground"
                )}
              >
                <LayoutDashboard className="w-4 h-4 inline mr-1.5" />
                {config.chartLabel}
              </button>
            </div>
          )}

          {/* Right: Voice Toggle + Profile Button */}
          <div className="flex items-center gap-2">
            {/* Voice Mode Toggle */}
            {onVoiceModeToggle && (
              <button
                onClick={onVoiceModeToggle}
                className={cn(
                  "px-3 py-1.5 text-xs font-medium rounded-full transition-all duration-200",
                  voiceMode === "warm"
                    ? "bg-orange-100 text-orange-700 dark:bg-orange-900/30 dark:text-orange-300"
                    : "bg-purple-100 text-purple-700 dark:bg-purple-900/30 dark:text-purple-300"
                )}
                title={voiceMode === "warm" ? "åˆ‡æ¢åˆ°åæ§½æ¨¡å¼" : "åˆ‡æ¢åˆ°æ¸©æš–æ¨¡å¼"}
              >
                {voiceMode === "warm" ? "ğŸŒ æ¸©æš–" : "ğŸ˜ˆ åæ§½"}
              </button>
            )}
            {onViewProfile && (
              <IconButton
                variant="ghost"
                size="sm"
                onClick={onViewProfile}
                aria-label={config.profileLabel}
              >
                <FileText className="w-5 h-5" />
              </IconButton>
            )}
          </div>
        </div>
      </header>

      {/* Main Content - Responsive Layout */}
      <div className="flex-1 overflow-hidden relative flex">
        {/* Left Sidebar - Insight Panel (PC only, >= 1024px) */}
        <aside className="hidden lg:block w-80 flex-shrink-0">
          <InsightPanel
            skill={skill}
            profile={profile}
            insights={insights}
            onViewFullProfile={onViewProfile}
            onInsightClick={onInsightClick}
          />
        </aside>

        {/* Right Content - Chat Area */}
        <div className="flex-1 flex flex-col min-w-0 overflow-hidden">
          {/* Mobile Insight Bar (< 1024px) */}
          <div className="lg:hidden">
            <MobileInsightBar
              skill={skill}
              profile={profile}
              onViewFullProfile={onViewProfile}
            />
          </div>

          {/* Chat Content */}
          <div className="flex-1 overflow-hidden">
            {children}
          </div>
        </div>
      </div>
    </LuminousPaper>
  );
}

