"use client";

/**
 * VoiceToggle - Voice mode toggle component
 * Based on: vibelife spec v3.0
 *
 * Switches between:
 * - Warm mode: æ¸©æš–ã€å…±æƒ…ã€æ”¯æŒ
 * - Sarcastic mode: ç›´æ¥ã€çŠ€åˆ©ã€æœ‰è¶£
 */

import { cn } from "@/lib/utils";
import { motion } from "framer-motion";

export type VoiceMode = "warm" | "sarcastic";

interface VoiceToggleProps {
  value: VoiceMode;
  onChange: (mode: VoiceMode) => void;
  size?: "sm" | "md" | "lg";
  showLabels?: boolean;
  className?: string;
}

const VOICE_CONFIG = {
  warm: {
    icon: "ğŸŒ",
    label: "æ¸©æš–",
    description: "å…±æƒ…ã€æ”¯æŒã€é¼“åŠ±",
    bgClass: "bg-orange-100 dark:bg-orange-900/30",
    textClass: "text-orange-700 dark:text-orange-300",
    borderClass: "border-orange-200 dark:border-orange-800",
  },
  sarcastic: {
    icon: "ğŸ˜ˆ",
    label: "åæ§½",
    description: "ç›´æ¥ã€çŠ€åˆ©ã€æœ‰è¶£",
    bgClass: "bg-purple-100 dark:bg-purple-900/30",
    textClass: "text-purple-700 dark:text-purple-300",
    borderClass: "border-purple-200 dark:border-purple-800",
  },
};

const SIZE_CONFIG = {
  sm: {
    padding: "px-2.5 py-1",
    text: "text-xs",
    gap: "gap-1",
    iconSize: "text-sm",
  },
  md: {
    padding: "px-3 py-1.5",
    text: "text-sm",
    gap: "gap-1.5",
    iconSize: "text-base",
  },
  lg: {
    padding: "px-4 py-2",
    text: "text-base",
    gap: "gap-2",
    iconSize: "text-lg",
  },
};

/**
 * Compact VoiceToggle - Single button that toggles between modes
 */
export function VoiceToggle({
  value,
  onChange,
  size = "md",
  showLabels = true,
  className,
}: VoiceToggleProps) {
  const config = VOICE_CONFIG[value];
  const sizeConfig = SIZE_CONFIG[size];

  const handleToggle = () => {
    onChange(value === "warm" ? "sarcastic" : "warm");
  };

  return (
    <motion.button
      onClick={handleToggle}
      className={cn(
        "flex items-center font-medium rounded-full transition-all duration-200",
        "border",
        sizeConfig.padding,
        sizeConfig.text,
        sizeConfig.gap,
        config.bgClass,
        config.textClass,
        config.borderClass,
        "hover:opacity-80 active:scale-95",
        className
      )}
      title={`å½“å‰ï¼š${config.label}æ¨¡å¼ - ${config.description}\nç‚¹å‡»åˆ‡æ¢`}
      whileTap={{ scale: 0.95 }}
    >
      <span className={sizeConfig.iconSize}>{config.icon}</span>
      {showLabels && <span>{config.label}</span>}
    </motion.button>
  );
}

/**
 * VoiceToggleSwitch - Dual-button toggle switch
 */
interface VoiceToggleSwitchProps {
  value: VoiceMode;
  onChange: (mode: VoiceMode) => void;
  className?: string;
}

export function VoiceToggleSwitch({
  value,
  onChange,
  className,
}: VoiceToggleSwitchProps) {
  return (
    <div
      className={cn(
        "inline-flex items-center p-1 rounded-full",
        "bg-muted/50 border border-border/50",
        className
      )}
    >
      {(["warm", "sarcastic"] as const).map((mode) => {
        const config = VOICE_CONFIG[mode];
        const isActive = value === mode;

        return (
          <button
            key={mode}
            onClick={() => onChange(mode)}
            className={cn(
              "relative px-3 py-1.5 rounded-full text-sm font-medium",
              "transition-all duration-200",
              isActive
                ? cn(config.bgClass, config.textClass, "shadow-sm")
                : "text-muted-foreground hover:text-foreground"
            )}
          >
            <span className="flex items-center gap-1.5">
              <span>{config.icon}</span>
              <span>{config.label}</span>
            </span>
          </button>
        );
      })}
    </div>
  );
}

/**
 * VoiceModeBadge - Display-only badge showing current voice mode
 */
interface VoiceModeBadgeProps {
  mode: VoiceMode;
  size?: "sm" | "md";
  className?: string;
}

export function VoiceModeBadge({
  mode,
  size = "sm",
  className,
}: VoiceModeBadgeProps) {
  const config = VOICE_CONFIG[mode];

  return (
    <span
      className={cn(
        "inline-flex items-center gap-1 rounded-full",
        size === "sm" ? "px-2 py-0.5 text-xs" : "px-2.5 py-1 text-sm",
        config.bgClass,
        config.textClass,
        className
      )}
    >
      <span>{config.icon}</span>
      <span>{config.label}æ¨¡å¼</span>
    </span>
  );
}

export default VoiceToggle;

