import { ChatContainer } from "@/components/chat/ChatContainer";
import { type SkillType } from "@/components/core";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Types
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

type VoiceMode = "warm" | "sarcastic";

const SKILL_CONFIG: Record<SkillType, { name: string; description: string }> = {
  bazi: {
    name: "å…«å­—å‘½ç†",
    description: "æ¢ç´¢ä½ çš„å‘½ç›˜ï¼Œäº†è§£å¤©èµ‹ä¸æ–¹å‘",
  },
  zodiac: {
    name: "æ˜Ÿåº§å æ˜Ÿ",
    description: "è§£è¯»ä½ çš„æ˜Ÿç›˜ï¼Œå‘ç°å†…åœ¨æ½œèƒ½",
  },
  // P1 skills (prepared for future)
  mbti: {
    name: "MBTI äººæ ¼",
    description: "å‘ç°ä½ çš„äººæ ¼ç±»å‹ï¼Œç†è§£è‡ªæˆ‘",
  },
  attach: {
    name: "ä¾æ‹æ¨¡å¼",
    description: "æ¢ç´¢äº²å¯†å…³ç³»ä¸­çš„ä¾æ‹é£æ ¼",
  },
  career: {
    name: "èŒä¸šå‘å±•",
    description: "è§„åˆ’ä½ çš„èŒä¸šè“å›¾ï¼Œæ‰¾åˆ°æ–¹å‘",
  },
};

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// Page Component
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export default function ChatPage() {
  // State
  const [skill, setSkill] = useState<SkillType>("bazi");
  const [voiceMode, setVoiceMode] = useState<VoiceMode>("warm");

  // Handlers
  const handleSkillChange = (newSkill: SkillType) => {
    setSkill(newSkill);
