"use client";

/**
 * Dev Login Page - Development Only
 *
 * Quick login for testing without OAuth.
 * Uses email or vibe_id to authenticate.
 */

import { useState, useCallback } from "react";
import { useRouter } from "next/navigation";
import { AuthLayout, AuthCard } from "@/components/auth/AuthLayout";

export default function DevLoginPage() {
  const router = useRouter();
  // é»˜è®¤æµ‹è¯•ç”¨æˆ· - å¯ä»¥åœ¨è¿™é‡Œä¿®æ”¹
  const DEFAULT_TEST_EMAIL = "iaaichina@163.com"; // ä¿®æ”¹ä¸ºä½ æƒ³è¦çš„é»˜è®¤é‚®ç®±
  const DEFAULT_TEST_VIBE_ID = ""; // æˆ–è€…ä½¿ç”¨ Vibe ID: VB-YRZ5YYVR

  const [email, setEmail] = useState(DEFAULT_TEST_EMAIL);
  const [vibeId, setVibeId] = useState(DEFAULT_TEST_VIBE_ID);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleLogin = useCallback(async () => {
    setError("");
    setLoading(true);

    try {
      // Use Next.js API route proxy to avoid CORS issues
      const response = await fetch("/api/auth/dev-login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          email: email || undefined,
          vibe_id: vibeId || undefined,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.detail || "Login failed");
      }

      // Store tokens
      localStorage.setItem("access_token", data.access_token);
      localStorage.setItem("refresh_token", data.refresh_token);
      localStorage.setItem("user", JSON.stringify(data.user));

      // Redirect to home
      router.push("/");
    } catch (err) {
      setError(err instanceof Error ? err.message : "Login failed");
    } finally {
      setLoading(false);
    }
  }, [email, vibeId, router]);

  return (
    <AuthLayout>
      <AuthCard
        title="Dev Login"
        subtitle="Development environment only"
      >
        {/* Warning Banner */}
        <div className="mb-4 p-3 rounded-lg bg-warning/10 border border-warning/20">
          <p className="text-warning text-sm text-center">
            This login method is only available in development mode
          </p>
        </div>

        {/* Error Display */}
        {error && (
          <div className="mb-4 p-3 rounded-lg bg-error/10 border border-error/20">
            <p className="text-error text-sm text-center">{error}</p>
          </div>
        )}

        {/* Email Input */}
        <div className="mb-4">
          <label className="block text-sm font-medium text-text-secondary mb-2">
            Email
          </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="test@vibelife.com"
            className="w-full px-4 py-3 rounded-lg border border-border-default bg-bg-secondary text-text-primary placeholder:text-text-tertiary focus:outline-none focus:ring-2 focus:ring-accent-primary/50"
          />
        </div>

        {/* Or Divider */}
        <div className="flex items-center my-4">
          <div className="flex-1 border-t border-border-default"></div>
          <span className="px-3 text-sm text-text-tertiary">or</span>
          <div className="flex-1 border-t border-border-default"></div>
        </div>

        {/* Vibe ID Input */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-text-secondary mb-2">
            Vibe ID
          </label>
          <input
            type="text"
            value={vibeId}
            onChange={(e) => setVibeId(e.target.value)}
            placeholder="VIBE-XXXXXXXX"
            className="w-full px-4 py-3 rounded-lg border border-border-default bg-bg-secondary text-text-primary placeholder:text-text-tertiary focus:outline-none focus:ring-2 focus:ring-accent-primary/50"
          />
        </div>

        {/* Login Button */}
        <button
          onClick={handleLogin}
          disabled={loading || (!email && !vibeId)}
          className="w-full py-3 px-4 rounded-lg bg-accent-primary text-white font-medium hover:bg-accent-primary/90 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
        >
          {loading ? "Logging in..." : "Login"}
        </button>

        {/* Back to OAuth */}
        <div className="mt-4 text-center">
          <a
            href="/auth/login"
            className="text-sm text-accent-primary hover:underline"
          >
            Back to OAuth Login
          </a>
        </div>
      </AuthCard>
    </AuthLayout>
  );
}

