/**
 * Dev Login API Route - Proxy to backend
 */

import { NextRequest, NextResponse } from "next/server";

const API_INTERNAL = process.env.VIBELIFE_API_INTERNAL || "http://127.0.0.1:8100";

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();

    const response = await fetch(`${API_INTERNAL}/api/v1/account/auth/dev-login`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(body),
    });

    const data = await response.json();
    return NextResponse.json(data, { status: response.status });
  } catch (error) {
    console.error("Dev login proxy error:", error);
    return NextResponse.json(
      { detail: "Failed to connect to backend" },
      { status: 500 }
    );
  }
}

