  // æŒ‰åˆ†ç±»åˆ†ç»„ Skills
  const skillsByCategory = useMemo(() => {
    const grouped: Record<string, SkillMetadata[]> = {
      professional: [],
      core: [], // åˆå¹¶ default å’Œ core åˆ°è¿™é‡Œ
    };

    for (const skill of skills) {
      if (skill.category === 'professional') {
        grouped.professional.push(skill);
      } else {
        // default å’Œ core éƒ½å½’å…¥ core
        grouped.core.push(skill);
      }
    }

    return grouped;
  }, [skills]);

  // ç²¾é€‰ Skill (é€‰æ‹©æœ€æœ‰ç‰¹è‰²çš„)
  const featuredSkill = useMemo(() => {
    // ä¼˜å…ˆé€‰æ‹© professional åˆ†ç±»çš„ç¬¬ä¸€ä¸ª
    return skillsByCategory.professional[0] || skillsByCategory.core[0] || skills[0];
  }, [skillsByCategory, skills]);

  // æ¨è Skills
  const recommendedSkills = useMemo(() => {
    const recommendedIds = new Set(recommendations.map(r => r.skill_id));
    return skills.filter(s => recommendedIds.has(s.id));
  }, [skills, recommendations]);

  // å¤„ç†è®¢é˜…
  const handleSubscribe = async (skillId: string) => {
    if (!user) {
      router.push('/auth/login?redirect=/chat');
      return;
    }
    try {
      await subscribe(skillId);
    } catch (error: any) {
      // å¤„ç† Premium è¦æ±‚é”™è¯¯ (402)
      if (error?.response?.status === 402) {
        const detail = error.response.data?.detail;
        if (detail?.code === 'PREMIUM_REQUIRED') {
          router.push(`/membership?reason=skill_subscribe&skill=${skillId}`);
          return;
        }
      }
      console.error('Subscribe failed:', error);
    }
  };

  // å¤„ç†å–æ¶ˆè®¢é˜…
  const handleUnsubscribe = async (skillId: string) => {
    if (!user) return;
    try {
      await unsubscribe(skillId);
    } catch (error) {
      console.error('Unsubscribe failed:', error);
    }
  };

  // å¤„ç†ç‚¹å‡» Skill
  const handleSkillClick = (skillId: string) => {
    router.push(`/skills/${skillId}`);
  };

  // Loading çŠ¶æ€
  if (isLoading) {
    return (
      <div className="h-full overflow-y-auto bg-bg-primary">
        <div className="max-w-7xl mx-auto px-4 lg:px-8 py-8 space-y-8">
          {/* Featured éª¨æ¶å± */}
          <div className="h-[400px] lg:h-[480px] rounded-3xl bg-bg-secondary animate-pulse" />

          {/* Section éª¨æ¶å± */}
          {[1, 2, 3].map((i) => (
            <div key={i} className="space-y-4">
              <div className="h-8 w-48 bg-bg-secondary animate-pulse rounded-lg" />
              <div className="flex gap-3 overflow-hidden">
                {[1, 2, 3].map((j) => (
                  <div key={j} className="w-64 h-48 bg-bg-secondary animate-pulse rounded-xl flex-shrink-0" />
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // é”™è¯¯çŠ¶æ€
  if (skillsError) {
    return (
      <div className="h-full overflow-y-auto bg-bg-primary">
        <div className="max-w-7xl mx-auto px-4 lg:px-8 py-8">
          <div className="flex flex-col items-center justify-center min-h-[60vh] space-y-6">
            <div className="w-20 h-20 rounded-full bg-red-500/10 flex items-center justify-center">
              <AlertCircle className="w-10 h-10 text-red-500" />
            </div>
            <div className="text-center space-y-2">
              <h2 className="text-2xl font-bold text-text-primary">åŠ è½½å¤±è´¥</h2>
              <p className="text-text-secondary max-w-md">
                æ— æ³•åŠ è½½ Skill åˆ—è¡¨ã€‚è¯·ç¨åé‡è¯•æˆ–è”ç³»æ”¯æŒã€‚
              </p>
            </div>
            <button
              onClick={() => window.location.reload()}
              className="px-6 py-3 rounded-full bg-accent-primary text-text-inverse font-medium hover:bg-accent-primary/90 transition-colors"
            >
              é‡æ–°åŠ è½½
            </button>
          </div>
        </div>
      </div>
    );
  }

  // ç©ºçŠ¶æ€
  if (!skills || skills.length === 0) {
    return (
      <div className="h-full overflow-y-auto bg-bg-primary">
        <div className="max-w-7xl mx-auto px-4 lg:px-8 py-8">
          <div className="flex flex-col items-center justify-center min-h-[60vh] space-y-6">
            <div className="w-20 h-20 rounded-full bg-accent-primary/10 flex items-center justify-center">
              <Sparkles className="w-10 h-10 text-accent-primary" />
            </div>
            <div className="text-center space-y-2">
              <h2 className="text-2xl font-bold text-text-primary">å³å°†æ¨å‡º</h2>
              <p className="text-text-secondary max-w-md">
                æˆ‘ä»¬æ­£åœ¨å‡†å¤‡æ›´å¤šç²¾å½©çš„ Skillsï¼Œæ•¬è¯·æœŸå¾…ï¼
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full overflow-y-auto bg-bg-primary">
      <div className="max-w-7xl mx-auto px-4 lg:px-8 py-8 space-y-12">
        {/* ç²¾é€‰æ¨ªå¹… */}
        {featuredSkill && (
          <FeaturedSkillBanner
            skill={featuredSkill}
            campaign={{
              title: 'ç²¾é€‰æ¨è',
              subtitle: 'å¼€å¯ä½ çš„è‡ªæˆ‘æ¢ç´¢ä¹‹æ—…',
              badge: 'ç¼–è¾‘æ¨è',
            }}
            onClick={() => handleSkillClick(featuredSkill.id)}
          />
        )}

        {/* ä¸ºä½ æ¨è */}
        {recommendedSkills.length > 0 && (
          <CategorySection
            title="ä¸ºä½ æ¨è"
            subtitle="åŸºäºä½ çš„ä½¿ç”¨ä¹ æƒ¯æ™ºèƒ½æ¨è"
            skills={recommendedSkills}
            userStatuses={userStatuses}
            onSkillClick={handleSkillClick}
            onSubscribe={handleSubscribe}
          />
        )}

        {/* ä¸“ä¸šæŠ€èƒ½ */}
        {skillsByCategory.professional.length > 0 && (
          <CategorySection
            title="ä¸“ä¸šæŠ€èƒ½"
            subtitle="æ·±åº¦æ¢ç´¢ï¼Œä¸“ä¸šæ´å¯Ÿ"
            skills={skillsByCategory.professional}
            userStatuses={userStatuses}
            onSkillClick={handleSkillClick}
            onSubscribe={handleSubscribe}
          />
        )}

        {/* æ ¸å¿ƒèƒ½åŠ›ï¼ˆåˆå¹¶äº†åŸºç¡€åŠŸèƒ½å’ŒåŸæ ¸å¿ƒèƒ½åŠ›ï¼‰*/}
        {skillsByCategory.core.length > 0 && (
          <CategorySection
            title="æ ¸å¿ƒèƒ½åŠ›"
            subtitle="äººäººå¯ç”¨çš„å®ç”¨å·¥å…·ï¼Œå§‹ç»ˆæ¿€æ´»åŠ©ä½ æˆé•¿"
            skills={skillsByCategory.core}
            userStatuses={userStatuses}
            onSkillClick={handleSkillClick}
            onSubscribe={handleSubscribe}
          />
        )}

        {/* åº•éƒ¨ç©ºç™½ */}
        <div className="h-8" />
      </div>
    </div>
  );
}

