    84â†’async function fetchAPI(
    85â†’  endpoint: string,
    86â†’  options: RequestInit = {}
    87â†’): Promise<Response> {
    88â†’  const { accessToken } = getTokens();
    89â†’
    90â†’  const headers: Record<string, string> = {
    91â†’    "Content-Type": "application/json",
    92â†’    ...options.headers as Record<string, string>,
    93â†’  };
    94â†’
    95â†’  if (accessToken) {
    96â†’    headers["Authorization"] = `Bearer ${accessToken}`;
    97â†’  }
    98â†’
    99â†’  const response = await fetch(`${API_BASE}${endpoint}`, {
   100â†’    ...options,
   101â†’    headers,
   102â†’  });
   103â†’
   104â†’  // Handle 401 - try to refresh token
   105â†’  if (response.status === 401) {
   106â†’    const { refreshToken } = getTokens();
   107â†’    if (refreshToken) {
   108â†’      const refreshed = await refreshAccessToken();
   109â†’      if (refreshed) {
   110â†’        // IMPORTANT: Get fresh token after refresh
   111â†’        const { accessToken: newAccessToken } = getTokens();
   112â†’        const newHeaders = {
   113â†’          ...headers,
   114â†’          "Authorization": `Bearer ${newAccessToken}`,
   115â†’        };
   116â†’        return fetch(`${API_BASE}${endpoint}`, { ...options, headers: newHeaders });
   117â†’      }
   118â†’    }
   119â†’  }
   120â†’
   121â†’  return response;
   122â†’}
