   229â†’    });
   230â†’
   231â†’    if (!response.ok) {
   232â†’      const error = await response.json().catch(() => ({ detail: "Stream failed" }));
   233â†’      onError(new Error(error.detail || "Stream failed"));
   234â†’      return;
   235â†’    }
   236â†’
   237â†’    const reader = response.body?.getReader();
   238â†’    const decoder = new TextDecoder();
   239â†’
   240â†’    if (!reader) {
   241â†’      onError(new Error("No response body"));
   242â†’      return;
   243â†’    }
   244â†’
   245â†’    let buffer = "";
   246â†’
   247â†’    while (true) {
   248â†’      const { done, value } = await reader.read();
   249â†’      if (done) break;
   250â†’
   251â†’      buffer += decoder.decode(value, { stream: true });
   252â†’      const lines = buffer.split("\n");
   253â†’      buffer = lines.pop() || "";
   254â†’
   255â†’      for (const line of lines) {
   256â†’        if (line.startsWith("data: ")) {
   257â†’          const dataStr = line.slice(6).trim();
   258â†’          if (!dataStr) continue;
   259â†’
   260â†’          try {
   261â†’            const data = JSON.parse(dataStr);
   262â†’            if (data.type === "chunk") {
   263â†’              onChunk(data.content);
   264â†’            } else if (data.type === "done") {
   265â†’              onDone({
   266â†’                conversation_id: data.conversation_id,
   267â†’                skill: data.skill,
   268â†’                voice_mode: data.voice_mode,
   269â†’              });
   270â†’              return;
   271â†’            } else if (data.type === "error") {
   272â†’              onError(new Error(data.message));
   273â†’              return;
   274â†’            }
   275â†’          } catch {
   276â†’            // Skip malformed JSON
   277â†’          }
   278â†’        }
   279â†’      }
   280â†’    }
   281â†’
   282â†’    onDone({});
   283â†’  } catch (error) {
   284â†’    onError(error instanceof Error ? error : new Error(String(error)));
   285â†’  }
   286â†’}
